import React, { useEffect, useState, useRef, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

const DetailContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0F172A 0%, #1E293B 100%);
  color: #fff;
  padding: 1rem;
  position: relative;
  overflow: hidden;

  @media (max-width: 768px) {
    padding: 0.5rem;
  }
`;

const PrintableContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: rgba(30, 41, 59, 0.8);
  border-radius: 24px;
  padding: 1.5rem;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(94, 234, 212, 0.2);
  box-shadow: 0 0 40px rgba(94, 234, 212, 0.1);
  position: relative;

  @media (max-width: 768px) {
    padding: 0.5rem;
    border-radius: 0;
    margin: 0;
    width: 100%;
    border: none;
  }
`;

const ItemHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;

  h1 {
    color: #5EEAD4;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(94, 234, 212, 0.3);
    font-weight: 700;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }

  .rarity-badge {
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    padding: 0.5rem 1.5rem;
    border-radius: 20px;
    font-weight: 600;
    display: inline-block;
    transform: translateY(-5px);
    animation: float 3s ease-in-out infinite;
  }
`;

const ItemImage = styled.div`
  position: relative;
  margin-bottom: 3rem;
  height: 400px;
  border-radius: 16px;
  overflow: hidden;

  &::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    background: linear-gradient(180deg, transparent 0%, rgba(15, 23, 42, 0.2) 100%);
  }
`;

const ItemSection = styled.section`
  margin-bottom: 2.5rem;
  opacity: 0;
  transform: translateY(20px);

  &.visible {
    animation: fadeIn 0.8s ease-out forwards;
  }

  &.map-section {
    .map-container {
      position: relative;
      padding: 20px;
      background: rgba(15, 23, 42, 0.5);
      border-radius: 16px;
      margin-top: 20px;
      opacity: 0;

      &.visible {
        animation: mapReveal 1.2s cubic-bezier(0.4, 0, 0.2, 1) forwards;
      }

      &::before {
        content: '';
        position: absolute;
        inset: 0;
        border-radius: 16px;
        padding: 2px;
        background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
        -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        -webkit-mask-composite: xor;
        mask-composite: exclude;
        animation: glowPulse 2s infinite;
      }
    }
  }

  &.story-section {
    p {
      position: relative;
      padding-left: 20px;

      &::before {
        content: '';
        position: absolute;
        left: 0;
        top: 0;
        width: 3px;
        height: 100%;
        background: linear-gradient(180deg, #5EEAD4, transparent);
        transform: scaleY(0);
        transform-origin: top;
        transition: transform 0.6s ease;
      }

      &.visible::before {
        transform: scaleY(1);
      }
    }
  }
`;

const PrintButton = styled.button`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  padding: 1rem 2rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  color: #0F172A;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 600;
  font-size: 1.1rem;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 0 30px rgba(94, 234, 212, 0.4);
  }

  @media (max-width: 768px) {
    display: none;
  }
`;

// 添加全局动画
const GlobalStyle = createGlobalStyle`
  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes glowPulse {
    0%, 100% { box-shadow: 0 0 20px rgba(94, 234, 212, 0.2); }
    50% { box-shadow: 0 0 40px rgba(94, 234, 212, 0.4); }
  }

  @keyframes mapReveal {
    0% { transform: scale(0.95); opacity: 0; }
    50% { transform: scale(1.02); }
    100% { transform: scale(1); opacity: 1; }
  }

  @keyframes slideInRight {
    from { transform: translateX(50px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
  }
`;

// 添加新的组件用于地图标记动画
const LocationMarker = styled.div`
  position: absolute;
  width: 20px;
  height: 20px;
  background: #5EEAD4;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  pointer-events: none;
  transition: all 0.3s ease;
  opacity: ${props => props.isScratched ? 1 : 0};

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: inherit;
    border-radius: inherit;
    animation: ripple 2s infinite;
  }

  @keyframes ripple {
    0% { transform: scale(1); opacity: 1; }
    100% { transform: scale(3); opacity: 0; }
  }
`;

// 添加新的样式组件
const ScratchCardContainer = styled.div`
  position: relative;
  width: 100%;
  margin: 20px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;
`;

const ScratchArea = styled.div`
  position: relative;
  width: 100%;
  border-radius: 16px;
  overflow: hidden;
  min-height: 150px;
`;

const ScratchHint = styled.div`
  color: #5EEAD4;
  font-size: 1.5rem;
  font-weight: bold;
  text-shadow: 
    0 0 10px rgba(94, 234, 212, 0.5),
    0 0 20px rgba(94, 234, 212, 0.3),
    0 0 30px rgba(94, 234, 212, 0.2);
  opacity: ${props => props.isScratched ? 0 : 1};
  transition: opacity 0.3s ease;
  animation: pulse 2s infinite;
  text-align: center;

  @keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
  }
`;

// 添加新的样式组件
const InfoContainer = styled.div`
  display: grid;
  gap: 1.5rem;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 0.75rem;
    padding: 0 0.5rem;
  }
`;

const InfoCard = styled.div`
  background: rgba(15, 23, 42, 0.5);
  border-radius: 16px;
  padding: 1.5rem;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    padding: 1px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(94, 234, 212, 0.2);
  }

  @media (max-width: 768px) {
    border-radius: 8px;
    padding: 1rem;

    &::before {
      border-radius: 8px;
    }

    &:hover {
      transform: none;
    }
  }
`;

const LocationCard = styled(InfoCard)`
  grid-column: 1 / -1;
  display: flex;
  align-items: center;
  gap: 1rem;
  background: linear-gradient(135deg, rgba(15, 23, 42, 0.8), rgba(30, 41, 59, 0.8));

  @media (max-width: 768px) {
    padding: 0.75rem;
    gap: 0.5rem;
  }
`;

const LocationIcon = styled.div`
  width: 48px;
  height: 48px;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  flex-shrink: 0;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: inherit;
    filter: blur(8px);
    opacity: 0.4;
    animation: pulse 2s infinite;
  }

  @keyframes pulse {
    0% { transform: scale(1); opacity: 0.4; }
    50% { transform: scale(1.2); opacity: 0.2; }
    100% { transform: scale(1); opacity: 0.4; }
  }
`;

const LocationInfo = styled.div`
  flex: 1;

  h3 {
    color: #5EEAD4;
    font-size: 1.2rem;
    margin: 0 0 0.5rem 0;
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }

  p {
    color: #94A3B8;
    margin: 0;
    font-size: 1rem;
    line-height: 1.6;
  }

  .location-tag {
    display: inline-block;
    padding: 0.25rem 0.75rem;
    background: rgba(94, 234, 212, 0.1);
    border-radius: 20px;
    color: #5EEAD4;
    font-size: 0.875rem;
    margin-top: 0.5rem;
    border: 1px solid rgba(94, 234, 212, 0.2);
  }
`;

const InfoLabel = styled.div`
  font-size: 0.875rem;
  color: #94A3B8;
  margin-bottom: 0.5rem;
`;

const InfoValue = styled.div`
  color: #5EEAD4;
  font-size: 1.1rem;
  font-weight: 500;
`;

const ScratchContent = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 30px;
  background: rgba(15, 23, 42, 0.8);
  border-radius: 16px;
  color: #5EEAD4;
  font-size: 1.3rem;
  text-align: center;
  letter-spacing: 1px;
  line-height: 1.6;
  font-weight: 500;

  p {
    margin: 0;
    text-shadow: 0 0 15px rgba(94, 234, 212, 0.5);
  }
`;

const ScratchCanvas = styled.canvas`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  cursor: pointer;
  touch-action: none;
`;

// 添加新的样式组件
const SecretSection = styled.div`
  margin-top: 2rem;
  background: rgba(15, 23, 42, 0.8);
  border-radius: 24px;
  padding: 2rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    inset: -2px;
    border-radius: 25px;
    padding: 2px;
    background: linear-gradient(45deg, #FF0080, #7928CA);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    animation: borderGlow 3s infinite;
  }

  @keyframes borderGlow {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  @media (max-width: 768px) {
    margin: 1rem 0;
    border-radius: 0;
    padding: 1rem;
  }
`;

const VerificationForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  align-items: center;
`;

const SecretTitle = styled.h3`
  color: #FF0080;
  text-align: center;
  font-size: 1.8rem;
  margin-bottom: 1.5rem;
  text-shadow: 0 0 15px rgba(255, 0, 128, 0.5);

  span {
    display: inline-block;
    animation: floatText 2s ease-in-out infinite;
  }
`;

const PasswordInput = styled.div`
  position: relative;
  width: 100%;
  max-width: 400px;

  input {
    width: 100%;
