function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const canvasRef = useRef(null);
  const [isScratched, setIsScratched] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });
  const [password, setPassword] = useState("");
  const [isVerified, setIsVerified] = useState(false);
  const [error, setError] = useState("");
  const [isFound, setIsFound] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [direction, setDirection] = useState(0);

  useEffect(() => {
    // 获取物品数据时检查是否已找到,如果已找到则自动解锁
    const fetchItemData = async () => {
      try {
        const mockItem = {
          id: parseInt(id),
          title: '神秘的青铜门把手',
          description: '这是一个雕刻着龙纹的古老门把，据说曾经属于一位著名的收藏家...',
          image: 'https://images.unsplash.com/photo-1461360228754-6e81c478b882',
          location: '知识花园区域',
          rarity: 'SSR',
          clue: '在书架与书架之间，寻找一扇被遗忘的门',
          story: '传说这个门把手曾经是通往一个秘密藏书室的钥匙，只有在月圆之夜才会显现其真正的模样。',
          hider: {
            name: "探索者小明",
            gender: "male",
            avatar: "https://via.placeholder.com/64",
            signature: "在知识的海洋中探索未知的奥秘",
            hiddenCount: 42,
            find: 156,
            tags: ["E人", "话痨", "快乐小狗"],
            status: "寻找志同道合的伙伴，一起探索未知的奥秘",
            isSingle : "不透露"
          },
          hideDate: '2024-01-15',
          additionalInfo: '这个门把手上的龙纹在特定角度下会泛出青色的光芒...',
          discoveryStory: '那是一个月圆之夜，我在理古籍时意外发现了这个门把手...',
          encrypted: true,
          password: "dragon888",
          secretContent: {
            title: "远古图书馆的秘密",
            additionalInfo: `
              在这个青铜门把手的背面，隐藏着一串古老的符文。经过专家鉴定，这些符文来自于失落的东方古文明。
              根据符文记载，这扇门通往一个被称为"万卷阁"的神秘图书馆，据说里面收藏着许多失传的古代典籍。

              🔍 重要发现：
              1. 门把手上的龙纹在满月之夜会发出微弱的蓝光
              2. 符文中提到了"子时"、"五行相生"等关键词
              3. 在把手底部发现了一个小机关装置
            `,
            discoveryStory: `
              那是一个特别的夜晚，图书馆已经关门，我正在整理最后一批古籍。突然间，月光透过窗户照在这个门把手上，
              原本普通的龙纹开始泛出奇异的青光。我注意到龙的眼睛位置有一个小凹槽，放入一枚古币大小的圆形物体后，
              门把手底部竟然弹出了一个暗格，里面藏着一张残破的羊皮纸，上面记载着这个神秘图书馆的线索...

              🗝️ 后续调查显示：
              - 这个门把手可能是古代某个秘密结社的信物
              - 类似的门把手在全球仅发现过3个
              - 其中蕴的机关技术远超当的工艺水平
            `,
            secretLocations: [
              "主图书馆东翼三楼的第七个书架",
              "古籍阅览室的暗格",
              "图书馆后花园的石亭"
            ],
            nextClue: "寻找刻有'五行'符号的石砖，那里藏着下一个线索..."
          },
          isFound: false, // 添加是否已找到的状态
          images: [
            'https://images.unsplash.com/photo-1461360228754-6e81c478b882',
            'https://images.unsplash.com/photo-1461360228754-6e81c478b882',
            'https://images.unsplash.com/photo-1461360228754-6e81c478b882'
          ],
        };
        setItem(mockItem);
        setIsFound(mockItem.isFound);

        // 如果物品已找到,自动设置验证状态为true
        if (mockItem.isFound) {
          setIsVerified(true);
        }
      } catch (error) {
        console.error('获取物品数据失败:', error);
      }
    };

    fetchItemData();
  }, [id]);

  const handlePrint = () => {
    window.print();
  };

  // 修改：将观察者逻辑移动到这个 useEffect 中，并添加 item 依赖
  useEffect(() => {
    if (!item) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.2 }
    );

    // 同时观察 ItemSection 和 map-container
    document.querySelectorAll('.item-section, .map-container').forEach(element => {
      observer.observe(element);
    });

    return () => observer.disconnect();
  }, [item]); // 添加 item 作为依赖

  // 初始化刮刮卡
  const initScratchCard = useCallback(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // 设置画布尺寸
    const updateCanvasSize = () => {
      const container = canvas.parentElement;
      canvas.width = container.offsetWidth;
      canvas.height = container.offsetHeight;

      // 填充初始遮罩
      ctx.fillStyle = '#1E293B';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // 添加一些花纹使其看起来更有趣
      ctx.strokeStyle = '#2DD4BF';
      for (let i = 0; i < canvas.width; i += 20) {
        for (let j = 0; j < canvas.height; j += 20) {
          ctx.beginPath();
          ctx.arc(i, j, 1, 0, Math.PI * 2);
          ctx.stroke();
        }
      }
    };

    updateCanvasSize();
    window.addEventListener('resize', updateCanvasSize);
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  // 处理刮擦效果
  const handleScratch = useCallback((e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const rect = canvas.getBoundingClientRect();

    // 获取鼠标/触摸位置
    const x = (e.clientX || e.touches[0].clientX) - rect.left;
    const y = (e.clientY || e.touches[0].clientY) - rect.top;

    ctx.globalCompositeOperation = 'destination-out';
    ctx.beginPath();
    ctx.arc(x, y, 20, 0, Math.PI * 2);
    ctx.fill();

    // 检查已刮开的面积
    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    const pixels = imageData.data;
    let transparentPixels = 0;

    for (let i = 0; i < pixels.length; i += 4) {
      if (pixels[i + 3] === 0) transparentPixels++;
    }

    const totalPixels = pixels.length / 4;
    if (transparentPixels / totalPixels > 0.5) {
      setIsScratched(true);
      canvas.style.display = 'none';
    }
  }, [isDrawing]);

  useEffect(() => {
    if (item) {
      const cleanup = initScratchCard();
      return cleanup;
    }
  }, [item, initScratchCard]);

  // 修改：更新鼠标移动处理函数
  const handleMouseMove = useCallback((e) => {
    if (!isScratched) return;

    const scratchArea = e.currentTarget;
    const rect = scratchArea.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;

    setMousePos({ x, y });
  }, [isScratched]);

  const handlePasswordVerification = async (e) => {
    e.preventDefault();
    if (password === item.password) {
      try {
        // 先设置验证成功状态，确保隐藏信息能显示
        setIsVerified(true);

        // 发送找到信息到后端
        const response = await fetch(`/api/items/${id}/found`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            foundAt: new Date().toISOString(),
          }),
        });

        if (response.ok) {
          setIsFound(true);
          setError("");
        } else {
          // 即使后端更新失败，也保持验证成功状态
          setError("状态更新失败，但您仍可以查看隐藏信息");
        }
      } catch (error) {
        console.error('发送找到信息失败:', error);
        // 即使API调用失败，也保持验证成功状态
        setError("状态更新失败，但您仍可以查看隐藏信息");
      }
    } else {
      setError("密码不正确，请重试");
      setIsVerified(false);
    }
  };

  // 添加自动轮播
  useEffect(() => {
    if (!item?.images?.length) return;

    const timer = setInterval(() => {
      setDirection(1);
      setCurrentImageIndex((prev) => (prev + 1) % item.images.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [item]);

  const handlePrevImage = () => {
    setDirection(-1);
    setCurrentImageIndex((prev) => 
      prev === 0 ? item.images.length - 1 : prev - 1
    );
  };

  const handleNextImage = () => {
    setDirection(1);
    setCurrentImageIndex((prev) => 
      (prev + 1) % item.images.length
    );
  };

  // 修改渲染部分，替换原来的 ItemImage 内容
  const renderCarousel = () => (
    <ItemImage>
      <CarouselContainer>
        <AnimatePresence initial={false} custom={direction}>
          <CarouselSlide
            key={currentImageIndex}
            custom={direction}
            initial={{ x: direction > 0 ? 1000 : -1000, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: direction > 0 ? -1000 : 1000, opacity: 0 }}
            transition={{ duration: 0.5 }}
          >
            <img 
              src={item.images[currentImageIndex]} 
              alt={`${item.title} - 图片 ${currentImageIndex + 1}`} 
            />
          </CarouselSlide>
        </AnimatePresence>

        {item.images.length > 1 && (
          <>
            <CarouselButton className="prev" onClick={handlePrevImage}>
              ←
            </CarouselButton>
            <CarouselButton className="next" onClick={handleNextImage}>
              →
            </CarouselButton>
            <CarouselDots>
              {item.images.map((_, index) => (
                <CarouselDot
                  key={index}
                  active={index === currentImageIndex}
                  onClick={() => {
                    setDirection(index > currentImageIndex ? 1 : -1);
                    setCurrentImageIndex(index);
                  }}
                />
              ))}
            </CarouselDots>
          </>
        )}
      </CarouselContainer>
    </ItemImage>
  );

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <DetailContainer>
      <GlobalStyle />
      <PrintableContent>
        <ItemHeader>
          <h1>{item.title}</h1>
          <p className="rarity-badge">{item.rarity}</p>
          {isFound && <FoundBadge>已找到</FoundBadge>}
        </ItemHeader>

        {renderCarousel()}

        <ItemSection className="item-section">
          <h2>📜 物品描述</h2>
          <p>{item.description}</p>
        </ItemSection>

        <ItemSection className="item-section map-section">
          <h2>🗺️ 寻宝线索</h2>
          <ScratchCardContainer>
            <ScratchHint isScratched={isScratched}>
              ✨ 刮开查看线索 ✨
            </ScratchHint>

            <ScratchArea onMouseMove={handleMouseMove}>
              <ScratchContent>
                <p>{item.clue}</p>
                <LocationMarker 
                  isScratched={isScratched}
                  style={{ 
                    left: `${mousePos.x}%`, 
                    top: `${mousePos.y}%`,
                    display: isScratched ? 'block' : 'none'
                  }} 
                />
              </ScratchContent>
              <ScratchCanvas
                ref={canvasRef}
                onMouseDown={() => setIsDrawing(true)}
                onMouseUp={() => setIsDrawing(false)}
                onMouseMove={handleScratch}
                onMouseLeave={() => setIsDrawing(false)}
                onTouchStart={() => setIsDrawing(true)}
                onTouchEnd={() => setIsDrawing(false)}
                onTouchMove={handleScratch}
              />
            </ScratchArea>
          </ScratchCardContainer>
        </ItemSection>

        <ItemSection className="item-section story-section">
          <h2>📖 背后的故事</h2>
          <p>{item.secretContent.title}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>🔍 发现过程</h2>
          <p>{item.secretContent.discoveryStory}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>ℹ️ 额外信息</h2>
          <p>{item.secretContent.additionalInfo}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>📍 基本信息</h2>
          <InfoContainer>
            <HiderCard>
              <HiderAvatar>
                <img src={item.hider.avatar || 'https://via.placeholder.com/64'} alt="隐藏者头像" />
              </HiderAvatar>
              <HiderInfo>
                <HiderName>
                  {item.hider.name}
                  <span className="gender-icon">♂</span>
                </HiderName>
                <HiderSignature>
                  {item.hider.signature || "探索未知的奥秘，分享发现的快乐"}
                </HiderSignature>
                <HiderStats>
                  <StatItem>
                    <span className="icon">🎯</span>
                    已隐藏 {item.hider.hiddenCount || 0} 件
                  </StatItem>
                  <StatItem>
                    <span className="icon">⭐</span>
                    找到 {item.hider.find || 0} 件
                  </StatItem>
                  <StatItem>
                    <span className="icon">💔</span>
                    {item.hider.isSingle}
                  </StatItem>
                </HiderStats>
              </HiderInfo>
            </HiderCard>

            <LocationCard>
              <LocationIcon>📍</LocationIcon>
              <LocationInfo>
                <h3>
                  探索地点
                  <span className="location-tag">重要线索</span>
                </h3>
                <p>{item.location}</p>
                <p className="location-tag">知识花园区域</p>
              </LocationInfo>
            </LocationCard>

            <InfoCard>
              <InfoLabel>隐藏者交友期待❤️</InfoLabel>
              <InfoValue>
                <span role="img" aria-label="explorer"></span> {item.hider.status}
              </InfoValue>
            </InfoCard>

            <InfoCard>
              <InfoLabel>隐藏者的标签🏷️</InfoLabel>
              <InfoValue>
                {item.hider.tags.map((tag, index) => (
                  <span key={index} style={{ marginRight: '0.5rem' }}>{tag}</span>
                ))}
              </InfoValue>
            </InfoCard>

            <InfoCard>
              <InfoLabel>隐藏时间🗓️</InfoLabel>
              <InfoValue>
                {item.hideDate}
              </InfoValue>
            </InfoCard>

          </InfoContainer>
        </ItemSection>
      </PrintableContent>

      <PrintButton onClick={handlePrint}>
        🖨️ 打印页面
      </PrintButton>

      {item?.encrypted && !isVerified && !isFound && (
        <SecretSection>
          <SecretTitle>
            {Array.from('神秘内容验证').map((char, i) => (
              <span key={i} style={{ animationDelay: `${i * 0.1}s` }}>{char}</span>
            ))}
          </SecretTitle>
          <VerificationForm onSubmit={handlePasswordVerification}>
            <PasswordInput>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="请输入通行密码..."
                autoComplete="off"
              />
            </PasswordInput>
            <VerifyButton type="submit">
              解锁内容
            </VerifyButton>
            {error && <ErrorMessage>{error}</ErrorMessage>}
          </VerificationForm>
        </SecretSection>
      )}

      {(isVerified || isFound) && item?.secretContent && (
        <UnlockedContent>
          <SecretTitle style={{ color: '#5EEAD4' }}>
            {Array.from('解密成功').map((char, i) => (
              <span key={i} style={{ animationDelay: `${i * 0.1}s` }}>{char}</span>
            ))}
          </SecretTitle>

          <ItemSection className="item-section">
            <h3>🔓 隐藏信息</h3>
            <p>{item.secretContent.additionalInfo}</p>
          </ItemSection>

          <ItemSection className="item-section">
            <h3>📖 完整发现记录</h3>
            <p>{item.secretContent.discoveryStory}</p>
          </ItemSection>

          <ItemSection className="item-section">
            <h3>📍 关键地点</h3>
            <ul>
              {item.secretContent.secretLocations.map((location, index) => (
                <li key={index} style={{color: '#94A3B8', marginBottom: '0.5rem'}}>
                  {location}
                </li>
              ))}
            </ul>
          </ItemSection>

          <ItemSection className="item-section">
            <h3>🗝️ 下一个线索</h3>
            <p style={{color: '#5EEAD4', fontStyle: 'italic'}}>
              {item.secretContent.nextClue}
            </p>
          </ItemSection>
        </UnlockedContent>
      )}
    </DetailContainer>
  );
}

export default ItemDetail;
