import React, { useEffect, useState, useRef, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';

const DetailContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0F172A 0%, #1E293B 100%);
  color: #fff;
  padding: 1rem;
  position: relative;
  overflow: hidden;

  @media (max-width: 768px) {
    padding: 0.5rem;
  }

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }
`;

const PrintableContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: rgba(30, 41, 59, 0.8);
  border-radius: 24px;
  padding: 1.5rem;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(94, 234, 212, 0.2);
  box-shadow: 0 0 40px rgba(94, 234, 212, 0.1);
  position: relative;

  @media (max-width: 768px) {
    padding: 1rem;
    border-radius: 16px;
    margin: 0;
    width: 100%;
  }

  &::before {
    content: '';
    position: absolute;
    inset: -2px;
    border-radius: 25px;
    padding: 2px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }
`;

const ItemHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;

  h1 {
    color: #5EEAD4;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(94, 234, 212, 0.3);
    font-weight: 700;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }

  .rarity-badge {
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    padding: 0.5rem 1.5rem;
    border-radius: 20px;
    font-weight: 600;
    display: inline-block;
    transform: translateY(-5px);
    animation: float 3s ease-in-out infinite;
  }
`;

const ItemImage = styled.div`
  position: relative;
  margin-bottom: 3rem;

  img {
    width: 100%;
    height: 400px;
    object-fit: cover;
    border-radius: 16px;
    border: 2px solid rgba(94, 234, 212, 0.3);
  }

  &::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    background: linear-gradient(180deg, transparent 0%, rgba(15, 23, 42, 0.2) 100%);
  }
`;

const ItemSection = styled.section`
  margin-bottom: 2.5rem;
  opacity: 0;
  transform: translateY(20px);

  &.visible {
    animation: fadeIn 0.8s ease-out forwards;
  }

  &.map-section {
    .map-container {
      position: relative;
      padding: 20px;
      background: rgba(15, 23, 42, 0.5);
      border-radius: 16px;
      margin-top: 20px;
      opacity: 0;

      &.visible {
        animation: mapReveal 1.2s cubic-bezier(0.4, 0, 0.2, 1) forwards;
      }

      &::before {
        content: '';
        position: absolute;
        inset: 0;
        border-radius: 16px;
        padding: 2px;
        background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
        -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        -webkit-mask-composite: xor;
        mask-composite: exclude;
        animation: glowPulse 2s infinite;
      }
    }
  }

  &.story-section {
    p {
      position: relative;
      padding-left: 20px;

      &::before {
        content: '';
        position: absolute;
        left: 0;
        top: 0;
        width: 3px;
        height: 100%;
        background: linear-gradient(180deg, #5EEAD4, transparent);
        transform: scaleY(0);
        transform-origin: top;
        transition: transform 0.6s ease;
      }

      &.visible::before {
        transform: scaleY(1);
      }
    }
  }
`;

const PrintButton = styled.button`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  padding: 1rem 2rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  color: #0F172A;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 600;
  font-size: 1.1rem;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 0 30px rgba(94, 234, 212, 0.4);
  }

  @media (max-width: 768px) {
    position: static;
    width: 100%;
    margin-top: 1rem;
    border-radius: 12px;
  }
`;

// æ·»åŠ å…¨å±€åŠ¨ç”»
const GlobalStyle = createGlobalStyle`
  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes glowPulse {
    0%, 100% { box-shadow: 0 0 20px rgba(94, 234, 212, 0.2); }
    50% { box-shadow: 0 0 40px rgba(94, 234, 212, 0.4); }
  }

  @keyframes mapReveal {
    0% { transform: scale(0.95); opacity: 0; }
    50% { transform: scale(1.02); }
    100% { transform: scale(1); opacity: 1; }
  }

  @keyframes slideInRight {
    from { transform: translateX(50px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
  }
`;

// æ·»åŠ æ–°çš„ç»„ä»¶ç”¨äºåœ°å›¾æ ‡è®°åŠ¨ç”»
const LocationMarker = styled.div`
  position: absolute;
  width: 20px;
  height: 20px;
  background: #5EEAD4;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  pointer-events: none;
  transition: all 0.3s ease;
  opacity: ${props => props.isScratched ? 1 : 0};

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: inherit;
    border-radius: inherit;
    animation: ripple 2s infinite;
  }

  @keyframes ripple {
    0% { transform: scale(1); opacity: 1; }
    100% { transform: scale(3); opacity: 0; }
  }
`;

// æ·»åŠ æ–°çš„æ ·å¼ç»„ä»¶
const ScratchCardContainer = styled.div`
  position: relative;
  width: 100%;
  margin: 20px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;
`;

const ScratchArea = styled.div`
  position: relative;
  width: 100%;
  border-radius: 16px;
  overflow: hidden;
  min-height: 150px;
`;

const ScratchHint = styled.div`
  color: #5EEAD4;
  font-size: 1.5rem;
  font-weight: bold;
  text-shadow: 
    0 0 10px rgba(94, 234, 212, 0.5),
    0 0 20px rgba(94, 234, 212, 0.3),
    0 0 30px rgba(94, 234, 212, 0.2);
  opacity: ${props => props.isScratched ? 0 : 1};
  transition: opacity 0.3s ease;
  animation: pulse 2s infinite;
  text-align: center;

  @keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
  }
`;

// ä¿®æ”¹ InfoContainer çš„æ ·å¼
const InfoContainer = styled.div`
  display: grid;
  gap: 1.5rem;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));

  @media (max-width: 768px) {
    grid-template-columns: 1fr; // æ”¹ä¸ºå•åˆ—
    gap: 1rem;
    padding: 0; // ç§»é™¤å†…è¾¹è·
    margin: 0; // ç§»é™¤å¤–è¾¹è·
    width: 100%; // ç¡®ä¿å®½åº¦100%
  }
`;

const InfoCard = styled.div`
  background: rgba(15, 23, 42, 0.5);
  border-radius: 16px;
  padding: 1.5rem;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    padding: 1px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(94, 234, 212, 0.2);
  }
`;

const LocationCard = styled(InfoCard)`
  grid-column: 1 / -1;
  display: flex;
  align-items: center;
  gap: 1rem;
  background: linear-gradient(135deg, rgba(15, 23, 42, 0.8), rgba(30, 41, 59, 0.8));

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }
`;

const LocationIcon = styled.div`
  width: 48px;
  height: 48px;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  flex-shrink: 0;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: inherit;
    filter: blur(8px);
    opacity: 0.4;
    animation: pulse 2s infinite;
  }

  @keyframes pulse {
    0% { transform: scale(1); opacity: 0.4; }
    50% { transform: scale(1.2); opacity: 0.2; }
    100% { transform: scale(1); opacity: 0.4; }
  }
`;

const LocationInfo = styled.div`
  flex: 1;

  h3 {
    color: #5EEAD4;
    font-size: 1.2rem;
    margin: 0 0 0.5rem 0;
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }

  p {
    color: #94A3B8;
    margin: 0;
    font-size: 1rem;
    line-height: 1.6;
  }

  .location-tag {
    display: inline-block;
    padding: 0.25rem 0.75rem;
    background: rgba(94, 234, 212, 0.1);
    border-radius: 20px;
    color: #5EEAD4;
    font-size: 0.875rem;
    margin-top: 0.5rem;
    border: 1px solid rgba(94, 234, 212, 0.2);
  }
`;

const InfoLabel = styled.div`
  font-size: 0.875rem;
  color: #94A3B8;
  margin-bottom: 0.5rem;
`;

const InfoValue = styled.div`
  color: #5EEAD4;
  font-size: 1.1rem;
  font-weight: 500;
`;

const ScratchContent = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 30px;
  background: rgba(15, 23, 42, 0.8);
  border-radius: 16px;
  color: #5EEAD4;
  font-size: 1.3rem;
  text-align: center;
  letter-spacing: 1px;
  line-height: 1.6;
  font-weight: 500;

  p {
    margin: 0;
    text-shadow: 0 0 15px rgba(94, 234, 212, 0.5);
  }
`;

const ScratchCanvas = styled.canvas`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  cursor: pointer;
