function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const canvasRef = useRef(null);
  const [isScratched, setIsScratched] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });
  const [password, setPassword] = useState("");
  const [isVerified, setIsVerified] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    // ËøôÈáåÂ∫îËØ•ÊòØ‰ªéAPIËé∑ÂèñÊï∞ÊçÆ
    // ÊöÇÊó∂‰ΩøÁî®Ê®°ÊãüÊï∞ÊçÆ
    const mockItem = {
      id: parseInt(id),
      title: 'Á•ûÁßòÁöÑÈùíÈìúÈó®ÊääÊâã',
      description: 'ËøôÊòØ‰∏Ä‰∏™ÈõïÂàªÁùÄÈæôÁ∫πÁöÑÂè§ËÄÅÈó®ÊääÔºåÊçÆËØ¥ÊõæÁªèÂ±û‰∫é‰∏Ä‰ΩçËëóÂêçÁöÑÊî∂ËóèÂÆ∂...',
      image: 'https://images.unsplash.com/photo-1461360228754-6e81c478b882',
      location: 'Áü•ËØÜËä±Âõ≠Âå∫Âüü',
      rarity: 'SSR',
      clue: 'Âú®‰π¶Êû∂‰∏é‰π¶Êû∂‰πãÈó¥ÔºåÂØªÊâæ‰∏ÄÊâáË¢´ÈÅóÂøòÁöÑÈó®',
      story: '‰º†ËØ¥Ëøô‰∏™Èó®ÊääÊâãÊõæÁªèÊòØÈÄöÂæÄ‰∏Ä‰∏™ÁßòÂØÜËóè‰π¶ÂÆ§ÁöÑÈí•ÂåôÔºåÂè™ÊúâÂú®ÊúàÂúÜ‰πãÂ§úÊâç‰ºöÊòæÁé∞ÂÖ∂ÁúüÊ≠£ÁöÑÊ®°Ê†∑„ÄÇ',
      discoveredBy: 'Êé¢Á¥¢ËÄÖÂ∞èÊòé',
      discoveryDate: '2024-01-15',
      additionalInfo: 'Ëøô‰∏™Èó®ÊääÊâã‰∏äÁöÑÈæôÁ∫πÂú®ÁâπÂÆöËßíÂ∫¶‰∏ã‰ºöÊ≥õÂá∫ÈùíËâ≤ÁöÑÂÖâËäí...',
      discoveryStory: 'ÈÇ£ÊòØ‰∏Ä‰∏™ÊúàÂúÜ‰πãÂ§úÔºåÊàëÂú®ÁêÜÂè§Á±çÊó∂ÊÑèÂ§ñÂèëÁé∞‰∫ÜËøô‰∏™Èó®ÊääÊâã...',
      encrypted: true,
      password: "1234"
    };

    setItem(mockItem);
  }, [id]);

  const handlePrint = () => {
    window.print();
  };

  // ‰øÆÊîπÔºöÂ∞ÜËßÇÂØüËÄÖÈÄªËæëÁßªÂä®Âà∞Ëøô‰∏™ useEffect ‰∏≠ÔºåÂπ∂Ê∑ªÂä† item ‰æùËµñ
  useEffect(() => {
    if (!item) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.2 }
    );

    // ÂêåÊó∂ËßÇÂØü ItemSection Âíå map-container
    document.querySelectorAll('.item-section, .map-container').forEach(element => {
      observer.observe(element);
    });

    return () => observer.disconnect();
  }, [item]); // Ê∑ªÂä† item ‰Ωú‰∏∫‰æùËµñ

  // ÂàùÂßãÂåñÂàÆÂàÆÂç°
  const initScratchCard = useCallback(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // ËÆæÁΩÆÁîªÂ∏ÉÂ∞∫ÂØ∏
    const updateCanvasSize = () => {
      const container = canvas.parentElement;
      canvas.width = container.offsetWidth;
      canvas.height = container.offsetHeight;

      // Â°´ÂÖÖÂàùÂßãÈÅÆÁΩ©
      ctx.fillStyle = '#1E293B';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Ê∑ªÂä†‰∏Ä‰∫õËä±Á∫π‰ΩøÂÖ∂ÁúãËµ∑Êù•Êõ¥ÊúâË∂£
      ctx.strokeStyle = '#2DD4BF';
      for (let i = 0; i < canvas.width; i += 20) {
        for (let j = 0; j < canvas.height; j += 20) {
          ctx.beginPath();
          ctx.arc(i, j, 1, 0, Math.PI * 2);
          ctx.stroke();
        }
      }
    };

    updateCanvasSize();
    window.addEventListener('resize', updateCanvasSize);
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  // Â§ÑÁêÜÂàÆÊì¶ÊïàÊûú
  const handleScratch = useCallback((e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const rect = canvas.getBoundingClientRect();

    // Ëé∑ÂèñÈº†Ê†á/Ëß¶Êë∏‰ΩçÁΩÆ
    const x = (e.clientX || e.touches[0].clientX) - rect.left;
    const y = (e.clientY || e.touches[0].clientY) - rect.top;

    ctx.globalCompositeOperation = 'destination-out';
    ctx.beginPath();
    ctx.arc(x, y, 20, 0, Math.PI * 2);
    ctx.fill();

    // Ê£ÄÊü•Â∑≤ÂàÆÂºÄÁöÑÈù¢ÁßØ
    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    const pixels = imageData.data;
    let transparentPixels = 0;

    for (let i = 0; i < pixels.length; i += 4) {
      if (pixels[i + 3] === 0) transparentPixels++;
    }

    const totalPixels = pixels.length / 4;
    if (transparentPixels / totalPixels > 0.5) {
      setIsScratched(true);
      canvas.style.display = 'none';
    }
  }, [isDrawing]);

  useEffect(() => {
    if (item) {
      const cleanup = initScratchCard();
      return cleanup;
    }
  }, [item, initScratchCard]);

  // ‰øÆÊîπÔºöÊõ¥Êñ∞Èº†Ê†áÁßªÂä®Â§ÑÁêÜÂáΩÊï∞
  const handleMouseMove = useCallback((e) => {
    if (!isScratched) return;

    const scratchArea = e.currentTarget;
    const rect = scratchArea.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;

    setMousePos({ x, y });
  }, [isScratched]);

  const handlePasswordVerification = (e) => {
    e.preventDefault();
    if (password === item.password) {
      setIsVerified(true);
      setError("");
    } else {
      setError("ÂØÜÁ†Å‰∏çÊ≠£Á°ÆÔºåËØ∑ÈáçËØï");
    }
  };

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <DetailContainer>
      <GlobalStyle />
      <PrintableContent>
        <ItemHeader>
          <h1>{item.title}</h1>
          <p className="rarity-badge">{item.rarity}</p>
        </ItemHeader>

        <ItemImage>
          <img src={item.image} alt={item.title} />
        </ItemImage>

        <ItemSection className="item-section">
          <h2>üìú Áâ©ÂìÅÊèèËø∞</h2>
          <p>{item.description}</p>
        </ItemSection>

        <ItemSection className="item-section map-section">
          <h2>üó∫Ô∏è ÂØªÂÆùÁ∫øÁ¥¢</h2>
          <ScratchCardContainer>
            <ScratchHint isScratched={isScratched}>
              ‚ú® ÂàÆÂºÄÊü•ÁúãÁ∫øÁ¥¢ ‚ú®
            </ScratchHint>

            <ScratchArea onMouseMove={handleMouseMove}>
              <ScratchContent>
                <p>{item.clue}</p>
                <LocationMarker 
                  isScratched={isScratched}
                  style={{ 
                    left: `${mousePos.x}%`, 
                    top: `${mousePos.y}%`,
                    display: isScratched ? 'block' : 'none'
                  }} 
                />
              </ScratchContent>
              <ScratchCanvas
                ref={canvasRef}
                onMouseDown={() => setIsDrawing(true)}
                onMouseUp={() => setIsDrawing(false)}
                onMouseMove={handleScratch}
                onMouseLeave={() => setIsDrawing(false)}
                onTouchStart={() => setIsDrawing(true)}
                onTouchEnd={() => setIsDrawing(false)}
                onTouchMove={handleScratch}
              />
            </ScratchArea>
          </ScratchCardContainer>
        </ItemSection>

        <ItemSection className="item-section story-section">
          <h2>üìñ ËÉåÂêéÁöÑÊïÖ‰∫ã</h2>
          <p>{item.story}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>üîç ÂèëÁé∞ËøáÁ®ã</h2>
          <p>{item.discoveryStory}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>‚ÑπÔ∏è È¢ùÂ§ñ‰ø°ÊÅØ</h2>
          <p>{item.additionalInfo}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>üìç Âü∫Êú¨‰ø°ÊÅØ</h2>
          <InfoContainer>
            <LocationCard>
              <LocationIcon>üìç</LocationIcon>
              <LocationInfo>
                <h3>
                  Êé¢Á¥¢Âú∞ÁÇπ
                  <span className="location-tag">ÈáçË¶ÅÁ∫øÁ¥¢</span>
                </h3>
                <p>{item.location}</p>
                <p className="location-tag">Áü•ËØÜËä±Âõ≠Âå∫Âüü</p>
              </LocationInfo>
            </LocationCard>

            <InfoCard>
              <InfoLabel>ÈöêËóèËÄÖ</InfoLabel>
              <InfoValue>
                <span role="img" aria-label="explorer">üîç</span> {item.discoveredBy}
              </InfoValue>
            </InfoCard>

            <InfoCard>
              <InfoLabel>ÈöêËóèÊó∂Èó¥</InfoLabel>
              <InfoValue>
                <span role="img" aria-label="calendar">üìÖ</span> {item.discoveryDate}
              </InfoValue>
            </InfoCard>
          </InfoContainer>
        </ItemSection>
      </PrintableContent>

      <PrintButton onClick={handlePrint}>
        üñ®Ô∏è ÊâìÂç∞È°µÈù¢
      </PrintButton>

      {item?.encrypted && !isVerified && (
        <SecretSection>
          <SecretTitle>
            {Array.from('Á•ûÁßòÂÜÖÂÆπÈ™åËØÅ').map((char, i) => (
              <span key={i} style={{ animationDelay: `${i * 0.1}s` }}>{char}</span>
            ))}
          </SecretTitle>
          <VerificationForm onSubmit={handlePasswordVerification}>
            <PasswordInput>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="ËØ∑ËæìÂÖ•ÈÄöË°åÂØÜÁ†Å..."
                autoComplete="off"
              />
            </PasswordInput>
            <VerifyButton type="submit">
              Ëß£ÈîÅÂÜÖÂÆπ
            </VerifyButton>
            {error && <ErrorMessage>{error}</ErrorMessage>}
          </VerificationForm>
        </SecretSection>
      )}

      {(!item?.encrypted || isVerified) && (
        <UnlockedContent>
          <SecretTitle style={{ color: '#5EEAD4' }}>
            {Array.from('Ëß£ÂØÜÊàêÂäü').map((char, i) => (
              <span key={i} style={{ animationDelay: `${i * 0.1}s` }}>{char}</span>
            ))}
          </SecretTitle>
          <ItemSection className="item-section">
            <h3>üîì ÈôÑÂä†‰ø°ÊÅØ</h3>
            <p>{item?.additionalInfo}</p>
          </ItemSection>
          <ItemSection className="item-section">
            <h3>üìñ ÂèëÁé∞ÊïÖ‰∫ã</h3>
            <p>{item?.discoveryStory}</p>
          </ItemSection>
        </UnlockedContent>
      )}
    </DetailContainer>
  );
}

export default ItemDetail;
