import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';


// Ê†∑ÂºèÂÆö‰πâ
const RegisterContainer = styled.div`
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2rem;
  position: relative;
  background: linear-gradient(135deg, #0F172A, #1E293B);
  overflow: hidden;

  @media (max-width: 768px) {
    padding: 0;
    background: linear-gradient(135deg, #0F172A, #1E293B);
    overflow: hidden;
  }
`;

const CosmicBackground = styled.div`
  position: absolute;
  inset: 0;
  overflow: hidden;
`;

const ShootingStar = styled.div`
  position: absolute;
  width: 2px;
  height: 2px;
  background: white;
  border-radius: 50%;
  animation: shooting-star linear forwards;

  @keyframes shooting-star {
    from {
      transform: translateX(0) translateY(0) rotate(-45deg);
      opacity: 1;
    }
    to {
      transform: translateX(150px) translateY(150px) rotate(-45deg);
      opacity: 0;
    }
  }
`;

const RegisterCard = styled.div`
  background: rgba(30, 41, 59, 0.8);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(94, 234, 212, 0.3);
  border-radius: 20px;
  padding: 2rem;
  width: 100%;
  max-width: 500px;
  position: relative;
  z-index: 1;
  box-shadow: 0 0 20px rgba(94, 234, 212, 0.2);

  @media (max-width: 768px) {
    border-radius: 0;
    max-width: 100%;
    min-height: 100vh;
    padding: 1.5rem;
    padding: 1rem;
    box-shadow: none;
    border: 1px solid rgba(94, 234, 212, 0.3);
    border-radius: 20px;
  }
`;

const CardTitle = styled.h1`
  text-align: center;
  color: #5EEAD4;
  font-size: 1.5rem;
  margin-bottom: 2rem;
  
  span {
    animation: twinkle 1.5s infinite;
  }

  @keyframes twinkle {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }
`;

const StepIndicator = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 2rem;
`;

const StepDot = styled.div`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: ${props => props.active ? '#5EEAD4' : 
    props.completed ? 'rgba(94, 234, 212, 0.5)' : 'rgba(94, 234, 212, 0.2)'};
  transition: all 0.3s ease;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const StepContent = styled.div`
  animation: fadeIn 0.3s ease;

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  color: #94A3B8;
  font-size: 0.9rem;
`;

const Input = styled.input`
  background: rgba(15, 23, 42, 0.3);
  border: 1px solid rgba(94, 234, 212, 0.3);
  border-radius: 8px;
  padding: 0.8rem;
  color: #fff;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: #5EEAD4;
    box-shadow: 0 0 0 2px rgba(94, 234, 212, 0.2);
  }
`;

const TextArea = styled.textarea`
  background: rgba(15, 23, 42, 0.3);
  border: 1px solid rgba(94, 234, 212, 0.3);
  border-radius: 8px;
  padding: 0.8rem;
  color: #fff;
  height: 100px;
  resize: none;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: #5EEAD4;
    box-shadow: 0 0 0 2px rgba(94, 234, 212, 0.2);
  }
`;

const AvatarUpload = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1.5rem;
`;

const AvatarPreview = styled.div`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  border: 2px solid rgba(94, 234, 212, 0.5);
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgba(15, 23, 42, 0.5);

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  span {
    font-size: 2rem;
  }
`;

const AvatarInput = styled.input`
  display: none;
`;

const UploadLabel = styled.label`
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  color: #0F172A;
  padding: 0.5rem 1.5rem;
  border-radius: 20px;
  cursor: pointer;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(94, 234, 212, 0.3);
  }
`;

const GenderSelect = styled.div`
  display: flex;
  gap: 1rem;
`;

const GenderOption = styled.button`
  flex: 1;
  padding: 0.8rem;
  border: 1px solid rgba(94, 234, 212, 0.3);
  border-radius: 8px;
  background: ${props => props.selected ? 'rgba(94, 234, 212, 0.2)' : 'transparent'};
  color: ${props => props.selected ? '#5EEAD4' : '#94A3B8'};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(94, 234, 212, 0.1);
  }
`;

const TagsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
`;

const TagButton = styled.button`
  padding: 0.5rem 1rem;
  border-radius: 15px;
  border: 1px solid rgba(94, 234, 212, 0.3);
  background: ${props => props.selected ? 'rgba(94, 234, 212, 0.2)' : 'transparent'};
  color: ${props => props.selected ? '#5EEAD4' : '#94A3B8'};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(94, 234, 212, 0.1);
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const StepButton = styled.button`
  flex: 1;
  padding: 0.8rem;
  border-radius: 8px;
  border: 1px solid rgba(94, 234, 212, 0.3);
  background: transparent;
  color: #5EEAD4;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(94, 234, 212, 0.1);
  }
`;

const SubmitButton = styled(StepButton)`
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border: none;
  color: #0F172A;
  font-weight: 600;

  &:hover {
    background: linear-gradient(45deg, #2DD4BF, #14B8A6);
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(94, 234, 212, 0.3);
  }
`;

const ErrorMessage = styled.div`
  color: #FF6B6B;
  font-size: 0.9rem;
  text-align: center;
`;



function Register() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    email: '',
    nickname: '',
    avatar: null,
    gender: '',
    signature: '',
    tags: [],
    status: 'Ê≠£Âú®ÂØªÊâæÂøóÂêåÈÅìÂêàÁöÑÊé¢Á¥¢ËÄÖ~',
  });
  
  const [currentStep, setCurrentStep] = useState(1);
  const [previewUrl, setPreviewUrl] = useState('');
  const [error, setError] = useState('');
  const [shootingStars, setShootingStars] = useState([]);
  const navigate = useNavigate();

  // È¢ÑËÆæÊ†áÁ≠æÈÄâÈ°π
  const tagOptions = [
    'Êé¢Á¥¢Ëææ‰∫∫', 'ÊïÖ‰∫ãÊî∂ÈõÜËÄÖ', 'ÂüéÂ∏ÇÊº´Ê∏∏', 'ÊñáÂåñÂØªÂÆù',
    'ÂéÜÂè≤Áà±Â•ΩËÄÖ', 'ÊëÑÂΩ±Áé©ÂÆ∂', 'Ëß£Ë∞úÈ´òÊâã', 'Á§æ‰∫§Ëææ‰∫∫'
  ];

  // Ê∑ªÂä†È™åËØÅÂáΩÊï∞
  const validateStep1 = () => {
    if (!formData.username.trim()) {
      setError('ËØ∑ËæìÂÖ•Áî®Êà∑Âêç');
      return false;
    }
    if (formData.password.length < 6) {
      setError('ÂØÜÁ†ÅÈïøÂ∫¶Ëá≥Â∞ë6‰Ωç');
      return false;
    }
    if (formData.password !== formData.confirmPassword) {
      setError('‰∏§Ê¨°ËæìÂÖ•ÁöÑÂØÜÁ†Å‰∏ç‰∏ÄËá¥');
      return false;
    }
    return true;
  };

  const validateStep2 = () => {
    if (!formData.nickname.trim()) {
      setError('ËØ∑ËæìÂÖ•ÊòµÁß∞');
      return false;
    }
    if (!formData.gender) {
      setError('ËØ∑ÈÄâÊã©ÊÄßÂà´');
      return false;
    }
    return true;
  };

  const validateStep3 = () => {
    if (!formData.signature.trim()) {
      setError('ËØ∑Â°´ÂÜô‰∏™ÊÄßÁ≠æÂêç');
      return false;
    }
    if (formData.tags.length === 0) {
      setError('ËØ∑Ëá≥Â∞ëÈÄâÊã©‰∏Ä‰∏™Ê†áÁ≠æ');
      return false;
    }
    return true;
  };

  // Â§ÑÁêÜÂ§¥ÂÉè‰∏ä‰º†
  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        setError('ÂõæÁâáÂ§ßÂ∞è‰∏çËÉΩË∂ÖËøá5MB');
        return;
      }
      setFormData({ ...formData, avatar: file });
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  // Â§ÑÁêÜÊ†áÁ≠æÈÄâÊã©
  const handleTagToggle = (tag) => {
    const newTags = formData.tags.includes(tag)
      ? formData.tags.filter(t => t !== tag)
      : [...formData.tags, tag].slice(0, 4);
    setFormData({ ...formData, tags: newTags });
  };

  // Ê∑ªÂä†ÊµÅÊòüÊïàÊûú
  useEffect(() => {
    const interval = setInterval(() => {
      const newStar = {
        id: Date.now(),
        top: Math.random() * 100,
        left: Math.random() * 100,
        duration: 1 + Math.random() * 2
      };
      setShootingStars(prev => [...prev, newStar]);
      setTimeout(() => {
        setShootingStars(prev => prev.filter(star => star.id !== newStar.id));
      }, newStar.duration * 1000);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  // ‰øÆÊîπÊ≠•È™§ÂàáÊç¢Â§ÑÁêÜ
  const handleNextStep = () => {
    setError(''); // Ê∏ÖÈô§‰πãÂâçÁöÑÈîôËØØ‰ø°ÊÅØ
    let isValid = false;
    
    switch (currentStep) {
      case 1:
        isValid = validateStep1();
        break;
      case 2:
        isValid = validateStep2();
        break;
      default:
        isValid = true;
    }

    if (isValid) {
      setCurrentStep(prev => prev + 1);
    }
  };

  // ‰øÆÊîπË°®ÂçïÊèê‰∫§Â§ÑÁêÜ
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    if (!validateStep3()) {
      return;
    }

    try {
      // Ê®°ÊãüAPIË∞ÉÁî®
      await new Promise(resolve => setTimeout(resolve, 1000));
      navigate('/explore');
    } catch (error) {
      setError('Ê≥®ÂÜåÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  return (
    <RegisterContainer>
      <CosmicBackground>
        {shootingStars.map(star => (
          <ShootingStar
            key={star.id}
            style={{
              top: `${star.top}%`,
              left: `${star.left}%`,
              animationDuration: `${star.duration}s`
            }}
          />
        ))}
      </CosmicBackground>

      <RegisterCard>
        <CardTitle>
          <span>‚ú®</span> ÂºÄÂêØ‰Ω†ÁöÑÊé¢Á¥¢‰πãÊóÖ <span>‚ú®</span>
        </CardTitle>

        <StepIndicator>
          {[1, 2, 3].map(step => (
            <StepDot key={step} active={step === currentStep} completed={step < currentStep} />
          ))}
        </StepIndicator>

        <Form onSubmit={handleSubmit}>
          {currentStep === 1 && (
            <StepContent>
              <FormGroup>
                <Label>Áî®Êà∑Âêç</Label>
                <Input
                  type="text"
                  value={formData.username}
                  onChange={e => setFormData({...formData, username: e.target.value})}
                  placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÔºàÁî®‰∫éÁôªÂΩïÔºâ"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>ÂØÜÁ†Å</Label>
                <Input
                  type="password"
                  value={formData.password}
                  onChange={e => setFormData({...formData, password: e.target.value})}
                  placeholder="ËØ∑ËÆæÁΩÆÂØÜÁ†Å"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>Á°ÆËÆ§ÂØÜÁ†Å</Label>
                <Input
                  type="password"
                  value={formData.confirmPassword}
                  onChange={e => setFormData({...formData, confirmPassword: e.target.value})}
                  placeholder="ËØ∑ÂÜçÊ¨°ËæìÂÖ•ÂØÜÁ†Å"
                  required
                />
              </FormGroup>
            </StepContent>
          )}

          {currentStep === 2 && (
            <StepContent>
              <AvatarUpload>
                <AvatarPreview>
                  {previewUrl ? (
                    <img src={previewUrl} alt="Â§¥ÂÉèÈ¢ÑËßà" />
                  ) : (
                    <span>üì∏</span>
                  )}
                </AvatarPreview>
                <AvatarInput
                  type="file"
                  accept="image/*"
                  onChange={handleAvatarChange}
                  id="avatar-upload"
                />
                <UploadLabel htmlFor="avatar-upload">
                  ÈÄâÊã©Â§¥ÂÉè
                </UploadLabel>
              </AvatarUpload>
              <FormGroup>
                <Label>ÊòµÁß∞</Label>
                <Input
                  type="text"
                  value={formData.nickname}
                  onChange={e => setFormData({...formData, nickname: e.target.value})}
                  placeholder="ÁªôËá™Â∑±Ëµ∑‰∏™Â•ΩÂê¨ÁöÑÊòµÁß∞Âêß"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>ÊÄßÂà´</Label>
                <GenderSelect>
                  <GenderOption
                    selected={formData.gender === 'male'}
                    onClick={() => setFormData({...formData, gender: 'male'})}
                  >
                    üë® Áî∑Áîü
                  </GenderOption>
                  <GenderOption
                    selected={formData.gender === 'female'}
                    onClick={() => setFormData({...formData, gender: 'female'})}
                  >
                    üë© Â•≥Áîü
                  </GenderOption>
                </GenderSelect>
              </FormGroup>
            </StepContent>
          )}

          {currentStep === 3 && (
            <StepContent>
              <FormGroup>
                <Label>‰∏™ÊÄßÁ≠æÂêç</Label>
                <TextArea
                  value={formData.signature}
                  onChange={e => setFormData({...formData, signature: e.target.value})}
                  placeholder="ÂÜô‰∏ã‰Ω†ÁöÑÊé¢Á¥¢ÂÆ£Ë®Ä..."
                  maxLength={50}
                />
              </FormGroup>
              <FormGroup>
                <Label>ÈÄâÊã©Ê†áÁ≠æÔºàÊúÄÂ§ö4‰∏™Ôºâ</Label>
                <TagsContainer>
                  {tagOptions.map(tag => (
                    <TagButton
                      key={tag}
                      selected={formData.tags.includes(tag)}
                      onClick={() => handleTagToggle(tag)}
                      type="button"
                    >
                      {tag}
                    </TagButton>
                  ))}
                </TagsContainer>
              </FormGroup>
            </StepContent>
          )}

          {error && <ErrorMessage>{error}</ErrorMessage>}

          <ButtonGroup>
            {currentStep > 1 && (
              <StepButton type="button" onClick={() => setCurrentStep(prev => prev - 1)}>
                ‰∏ä‰∏ÄÊ≠•
              </StepButton>
            )}
            {currentStep < 3 ? (
              <StepButton type="button" onClick={handleNextStep}>
                ‰∏ã‰∏ÄÊ≠•
              </StepButton>
            ) : (
              <SubmitButton type="submit">ÂºÄÂêØÊé¢Á¥¢‰πãÊóÖ</SubmitButton>
            )}
          </ButtonGroup>
        </Form>
      </RegisterCard>
    </RegisterContainer>
  );
}

export default Register;
