function Upload() {
  // 添加表单状态
  const [formData, setFormData] = useState({
    name: '',
    rarity: 'N',
    description: '',
    location: '',
    clue: '',
    image: null,
    password: '',
    hiddenInfo: ''
  });

  // 处理输入变化
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    console.log(`${name} changed to:`, value); // 调试日志
  };

  // 处理图片上传
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setFormData(prev => ({
      ...prev,
      image: file
    }));
    console.log('Selected image:', file); // 调试日志
  };

  // 处理表单提交
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data:', formData); // 调试日志
  };

  return (
    <UploadContainer>
      <UploadContent>
        <Header>
          <h1>上传物品</h1>
          <p>分享你的发现，让更多人参与寻宝的乐趣</p>
        </Header>

        <UploadForm onSubmit={handleSubmit}>
          <FormSection>
            <h3>基本信息</h3>
            <InputGroup>
              <label>物品名称</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder="给你的物品起个独特的名字"
              />
            </InputGroup>

            <InputGroup>
              <label>稀有度</label>
              <select
                name="rarity"
                value={formData.rarity}
                onChange={handleInputChange}
              >
                <option value="N">N - 普通</option>
                <option value="R">R - 稀有</option>
                <option value="SR">SR - 超稀有</option>
                <option value="SSR">SSR - 绝世珍品</option>
              </select>
            </InputGroup>

            <InputGroup>
              <label>物品描述</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="详细描述一下这个物品的特点..."
              />
            </InputGroup>
          </FormSection>

          <FormSection>
            <h3>位置信息</h3>
            <InputGroup>
              <label>藏匿地点</label>
              <select
                name="location"
                value={formData.location}
                onChange={handleInputChange}
              >
                <option value="A">A - 天台区域3楼</option>
                <option value="B">B - 前门区域</option>
                <option value="C">C - 知识花园</option>
                <option value="D">D - 创意实验室</option>

              </select>
            </InputGroup>

            <InputGroup>
              <label>寻宝线索</label>
              <textarea
                name="clue"
                value={formData.clue}
                onChange={handleInputChange}
                placeholder="给寻宝者一些提示..."
              />
            </InputGroup>
          </FormSection>

          <FormSection>
            <h3>图片上传</h3>
            <ImageUpload>
              <input
                type="file"
                accept="image/*"
                id="imageUpload"
                onChange={handleImageUpload}
              />
              <label htmlFor="imageUpload">
                <span role="img" aria-label="upload">📸</span>
                <p>点击或拖拽上传图片</p>
              </label>
            </ImageUpload>
          </FormSection>

          <FormSection>
            <h3>神秘内容</h3>
            <InputGroup>
              <label>验证密码</label>
              <input
                type="text"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="设置一个密码，用于解锁隐藏内容"
              />
            </InputGroup>

            <InputGroup>
              <label>隐藏信息</label>
              <textarea
                name="hiddenInfo"
                value={formData.hiddenInfo}
                onChange={handleInputChange}
                placeholder="这些信息只有通过验证后才能看到..."
              />
            </InputGroup>
          </FormSection>

          <SubmitButton type="submit">
            发布物品
          </SubmitButton>
        </UploadForm>
      </UploadContent>
    </UploadContainer>
  );
}

export default Upload;
