import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

// 动画效果
const float = keyframes`
  0% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
  100% { transform: translateY(0px) rotate(0deg); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #FF6B6B, 0 0 20px #FF6B6B; }
  100% { box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #FF6B6B, 0 0 40px #FF6B6B; }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const rocketFly = keyframes`
  0% {
    transform: translateY(100vh) rotate(-45deg);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateY(-100vh) rotate(-45deg);
    opacity: 0;
  }
`;

const fadeInScale = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.8);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
`;

const HomeContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  padding: 2rem;
  background: #1a1a1a;
  color: #fff;
`;

const LightBeam = styled.div`
  position: absolute;
  width: 2px;
  height: 100%;
  background: linear-gradient(to bottom, transparent, #FF6B6B, transparent);
  opacity: 0.5;
  transform-origin: 50% 0;
  animation: ${float} 3s ease-in-out infinite;
  left: ${props => props.left}%;
  transform: rotate(${props => props.rotate}deg);
`;

const Planet = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: ${props => props.color};
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  animation: ${float} ${props => props.duration}s ease-in-out infinite;
  z-index: 1;
  box-shadow: 0 0 20px rgba(255,255,255,0.2);

  &::after {
    content: '';
    position: absolute;
    width: 110%;
    height: 110%;
    border-radius: 50%;
    border: 2px solid rgba(255,255,255,0.2);
    left: -5%;
    top: -5%;
    animation: ${pulse} 4s ease-in-out infinite;
  }
`;

const Title = styled.h1`
  font-size: 4.5rem;
  font-weight: bold;
  color: #fff;
  margin-bottom: 1rem;
  text-align: center;
  position: relative;
  z-index: 2;
  text-shadow: 0 0 10px rgba(255,107,107,0.5);
  animation: ${glow} 2s ease-in-out infinite alternate;
`;

const Subtitle = styled.p`
  font-size: 1.8rem;
  color: #FF6B6B;
  margin-bottom: 2rem;
  text-align: center;
  font-family: 'Noto Sans SC', sans-serif;
  position: relative;
  z-index: 2;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 2rem;
  margin-top: 2rem;
  position: relative;
  z-index: 2;
`;

const StyledButton = styled(Link)`
  padding: 1.2rem 2.5rem;
  font-size: 1.2rem;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      120deg,
      transparent,
      rgba(255, 255, 255, 0.2),
      transparent
    );
    transition: 0.5s;
  }

  &:hover::before {
    left: 100%;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 20px rgba(255,107,107,0.4);
  }

  &.primary {
    background: #FF6B6B;
    color: white;
  }

  &.secondary {
    background: transparent;
    border: 2px solid #4ECDC4;
    color: #4ECDC4;
  }
`;

const FunText = styled.span`
  position: absolute;
  font-size: 1rem;
  color: #FFE66D;
  transform: rotate(${props => props.rotate}deg);
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  z-index: 2;
`;

const Rocket = styled.div`
  position: fixed;
  width: 60px;
  height: 60px;
  right: 10%;
  z-index: 10;
  opacity: 0;
  pointer-events: none;
  
  &.flying {
    animation: ${rocketFly} 2s ease-in-out forwards;
  }

  &::before {
    content: '🚀';
    font-size: 40px;
    position: absolute;
    transform: rotate(45deg);
  }

  &::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 100px;
    background: linear-gradient(to bottom, #FF6B6B, transparent);
    transform: translateY(20px) rotate(45deg);
    filter: blur(10px);
    opacity: 0.5;
  }
`;

const ImageFrame = styled.div`
  position: relative;
  width: 300px;
  height: 200px;
  margin: 3rem auto;
  opacity: 0.8;
  transform: scale(0.8);

  &.visible {
    animation: ${fadeInScale} 1s ease-out forwards;
  }

  &::before {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    right: -20px;
    bottom: -20px;
    border: 2px solid #FF6B6B;
    border-radius: 10px;
    background: rgba(40, 40, 40, 0.8);
    backdrop-filter: blur(10px);
    z-index: -1;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 5px;
  }
`;

const ScrollSection = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  padding: 2rem;
`;

// 添加新的动画关键帧
const flyUpAnimation = keyframes`
  0% {
    transform: translate(var(--startX), 100vh) rotate(var(--rotation));
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translate(var(--endX), -100vh) rotate(var(--finalRotation));
    opacity: 0;
  }
`;

const FloatingElement = styled.div`
  position: fixed;
  font-size: ${props => props.size || '2rem'};
  z-index: 5;
  pointer-events: none;
  animation: ${flyUpAnimation} var(--duration) ease-out forwards;
  left: 0;
  top: 0;
  transform: translateY(100vh);
`;

const FLOATING_ELEMENTS = [
  '🚀', '✨', '🎸', '🎹', '🎺', '🎭', '🎨', '🎮', 
  '🌟', '🌍', '🌘', '🎯', '🎪', '🎵', '🎼', '🎧'
];

function getRandomValue(min, max) {
  return Math.random() * (max - min) + min;
}

function FloatingElements() {
  const [elements, setElements] = useState([]);

  useEffect(() => {
    const createNewElement = () => {
      const id = Date.now();
      const element = FLOATING_ELEMENTS[Math.floor(Math.random() * FLOATING_ELEMENTS.length)];
      const startX = getRandomValue(-20, window.innerWidth - 50);
      const endX = startX + getRandomValue(-200, 200);
      const duration = getRandomValue(3, 8);
      const size = getRandomValue(1.5, 3) + 'rem';
      const rotation = getRandomValue(-45, 45);
      const finalRotation = rotation + getRandomValue(-180, 180);

      setElements(prev => [...prev, {
        id,
        element,
        style: {
          '--startX': `${startX}px`,
          '--endX': `${endX}px`,
          '--duration': `${duration}s`,
          '--rotation': `${rotation}deg`,
          '--finalRotation': `${finalRotation}deg`
        },
        size
      }]);

      // 动画结束后移除元素
      setTimeout(() => {
        setElements(prev => prev.filter(e => e.id !== id));
      }, duration * 1000);
    };

    // 初始创建几个元素
    for (let i = 0; i < 5; i++) {
      setTimeout(createNewElement, i * 500);
    }

    // 定期创建新元素
    const interval = setInterval(() => {
      if (Math.random() < 0.7) { // 70%的概率创建新元素
        createNewElement();
      }
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return elements.map(({ id, element, style, size }) => (
    <FloatingElement key={id} style={style} size={size}>
      {element}
    </FloatingElement>
  ));
}

// 修改流星动画
const meteorFall = keyframes`
  0% {
    transform: translate(-120%, 20%) rotate(15deg);
    opacity: 0;
  }
  5% {
    opacity: 1;
  }
  90% {
    opacity: 1;
    transform: translate(80%, -10%) rotate(15deg);
  }
  95% {
    opacity: ${props => props.persist ? 1 : 0};
    transform: translate(100%, -15%) rotate(15deg);
  }
  100% {
    transform: translate(120%, -20%) rotate(15deg);
    opacity: ${props => props.persist ? 1 : 0};
  }
`;

// 修改 Meteor 组件样式
const Meteor = styled.div`
  position: absolute;
  width: ${props => props.width || '200px'};
  height: 3px;
  background: linear-gradient(
    270deg,
    transparent,
    ${props => props.color || '#FF6B6B'}
  );
  top: ${props => props.top}%;
  left: 0;
  filter: blur(3px);
  opacity: 0;
  z-index: 1;
  transform-origin: right center;
  animation: ${props => meteorFall} ${props => props.duration || 3}s linear;
  animation-delay: ${props => props.delay || '0s'};
  animation-iteration-count: ${props => props.persist ? 'infinite' : 1};
  animation-fill-mode: ${props => props.persist ? 'none' : 'forwards'};

  &::after {
    content: '';
    position: absolute;
    width: 15px;
    height: 15px;
    border-radius: 50%;
    background: ${props => props.color || '#FF6B6B'};
    right: -2px;
    top: 50%;
    transform: translateY(-50%);
    filter: blur(4px);
    box-shadow: 
      0 0 20px ${props => props.color || '#FF6B6B'},
      0 0 40px ${props => props.color || '#FF6B6B'}33;
  }

  &::before {
    content: '';
    position: absolute;
    width: 150%;
    height: 100%;
    background: linear-gradient(
      270deg,
      transparent,
      ${props => props.color || '#FF6B6B'}33,
      transparent
    );
    right: 0;
    transform: translateX(25%);
    filter: blur(10px);
  }
`;

// 修改生成流星的函数
const generateMeteors = () => {
  return Array.from({ length: 12 }, () => ({
    color: ['#FF6B6B', '#4ECDC4', '#FFE66D'][Math.floor(Math.random() * 3)],
    top: Math.random() * 60 + 5,
    duration: Math.random() * 6 + 6, // 6-12秒
    delay: Math.random() * 8, // 0-8秒延迟
    width: Math.random() * 100 + 150 + 'px', // 150-250px
    persist: Math.random() < 0.3, // 30%的概率永久存在
  }));
};

// 创建一个新的城市剪影组件
const CityScapeContainer = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 35vh;
  background: linear-gradient(to bottom, transparent 0%, rgba(26, 26, 26, 0.8) 40%);
  z-index: 2;
  overflow: hidden;
`;

const Buildings = styled.div`
  position: absolute;
  bottom: 0;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(to top, #1a1a1a 0%, transparent 50%),
    repeating-linear-gradient(
      90deg,
      rgba(255, 255, 255, 0.05) 0px,
      rgba(255, 255, 255, 0.05) 1px,
      transparent 1px,
      transparent 30px
    );
  &::before {
    content: '';
    position: absolute;
    bottom: 0;
    width: 100%;
    height: 60%;
    background: 
      linear-gradient(to bottom, transparent, #1a1a1a),
      repeating-linear-gradient(
        90deg,
        #1a1a1a,
        #1a1a1a 40px,
        #222 40px,
        #222 80px
      );
  }
`;

const BuildingLights = styled.div`
  position: absolute;
  bottom: 0;
  width: 100%;
  height: 100%;
  background-image: radial-gradient(
    circle at center,
    rgba(255, 255, 255, 0.1) 0%,
    transparent 1%
  );
  background-size: 40px 40px;
  animation: twinkle 3s infinite alternate;
`;

// 添加新的样式组件
const CitySection = styled(ScrollSection)`
  background: #1a1a1a;
  position: relative;
  overflow: hidden;
  padding: 0;
  margin: 0;
`;

const CityScape = styled.div`
  position: absolute;
  bottom: 0;
  width: 100%;
  height: 60%;
  background: linear-gradient(to bottom, transparent, #1a1a1a),
    url('/city-silhouette.png') repeat-x bottom;
  background-size: contain;
  z-index: 2;
`;

const NeonPlanet = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: ${props => props.color};
  box-shadow: 0 0 30px ${props => props.color}aa,
             inset -10px -10px 20px rgba(0,0,0,0.5);
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  z-index: ${props => props.zIndex || 1};
  animation: ${float} ${props => props.duration || 6}s ease-in-out infinite;
  animation-delay: ${props => props.delay || '0s'};

  &::after {
    content: '';
    position: absolute;
    width: 120%;
    height: 120%;
    border-radius: 50%;
    border: 2px solid ${props => props.color}44;
    left: -10%;
    top: -10%;
    animation: ${pulse} 4s ease-in-out infinite;
  }
`;

// 添加发射动画
const shootUp = keyframes`
  0% {
    transform: translateY(0) scale(1);
    opacity: 1;
  }
  50% {
    transform: translateY(-50vh) scale(1.5);
    opacity: 0.5;
  }
  100% {
    transform: translateY(-100vh) scale(0.5);
    opacity: 0;
  }
`;

// 修改 ButtonBar 样式
const ButtonBar = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 80px;
  background: linear-gradient(to top, #1a1a1a, transparent);
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  z-index: 10;
`;

const IconButton = styled.button`
  background: transparent;
  border: none;
  color: ${props => props.color || '#4ECDC4'};
  font-size: 1.5rem;
  cursor: pointer;
  position: relative;
  padding: 1rem;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: ${props => props.color || '#4ECDC4'}22;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    transition: all 0.3s ease;
  }

  &:hover::before {
    transform: translate(-50%, -50%) scale(1.2);
  }

  &:active::before {
    transform: translate(-50%, -50%) scale(0.9);
  }
`;

const ShootingIcon = styled.div`
  position: fixed;
  font-size: 2rem;
  pointer-events: none;
  z-index: 100;
  animation: ${shootUp} 1s ease-out forwards;
  color: ${props => props.color};
  text-shadow: 0 0 10px ${props => props.color};
`;

// 添加新的动画效果
const slideInFromRight = keyframes`
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

const CardSection = styled.div`
  min-height: 100vh;
  width: 100%;
  padding: 4rem 2rem;
  position: relative;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
  z-index: 2;
  background: #1a1a1a;
`;

const SimpleStoryCard = styled.div`
  background: rgba(30, 30, 30, 0.8);
  border-radius: 20px;
  padding: 2rem;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  transform: translateY(50px);
  opacity: 0;
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  will-change: transform, opacity;
  cursor: pointer;
  position: relative;
  overflow: hidden;

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }

  &:hover {
    transform: translateY(-10px);
    border-color: ${props => props.accentColor || '#FF6B6B'};
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      45deg,
      transparent,
      rgba(255, 255, 255, 0.1),
      transparent
    );
    transform: translateX(-100%);
    transition: 0.5s;
  }

  &:hover::before {
    transform: translateX(100%);
  }
`;

const SimpleCardImage = styled.div`
  width: 100%;
  height: 200px;
  border-radius: 10px;
  overflow: hidden;
  margin-bottom: 1rem;
  position: relative;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  &:hover img {
    transform: scale(1.1);
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      to bottom,
      transparent,
      rgba(0, 0, 0, 0.5)
    );
  }
`;

const SimpleCardTitle = styled.h3`
  font-size: 1.5rem;
  color: #fff;
  margin-bottom: 1rem;
`;

const SimpleCardDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
  margin-bottom: 1rem;
`;

const SimpleCardFooter = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 1rem;
`;

const CardStats = styled.div`
  display: flex;
  gap: 1rem;
  color: rgba(255, 255, 255, 0.6);
  font-size: 0.9rem;

  span {
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }
`;

// 修改卡片容器样式
const StoryContainer = styled.div`
  position: relative;
  min-height: 100vh;
  background: linear-gradient(
    to bottom,
    transparent,
    rgba(26, 26, 26, 0.95) 10%,
    #1a1a1a 30%
  );
  padding: 4rem 2rem;
  z-index: 3;
`;

const StoryHeader = styled.div`
  text-align: center;
  margin-bottom: 4rem;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s ease-out;

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }

  h2 {
    font-size: 2.5rem;
    color: #fff;
    margin-bottom: 1rem;
    text-shadow: 0 0 10px rgba(255,107,107,0.5);
  }

  p {
    color: rgba(255,255,255,0.8);
    font-size: 1.2rem;
  }
`;

// 修改卡片网格样式
const StoryGrid = styled.div`
  display: grid;
