// 卡片基础样式
const StoryCard = styled.div`
  background: rgba(30, 30, 30, 0.9);
  border-radius: 20px;
  overflow: hidden;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at top right, 
      rgba(255, 107, 107, 0.1),
      transparent 60%);
    z-index: -1;
    opacity: 0;
    transition: opacity 0.4s ease;
  }

  &:hover {
    transform: translateY(-10px);
    border-color: ${props => props.accentColor || '#FF6B6B'};
    box-shadow: 
      0 10px 30px rgba(0, 0, 0, 0.3),
      0 0 20px ${props => props.accentColor || '#FF6B6B'}33,
      0 0 40px ${props => props.accentColor || '#FF6B6B'}22;

    &::before {
      opacity: 1;
    }
  }
`;

const CardHeader = styled.div`
  position: relative;
  height: 200px;
  overflow: hidden;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      to bottom,
      transparent,
      rgba(26, 26, 26, 0.8)
    );
  }
`;

const CardImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.6s ease;

  ${StoryCard}:hover & {
    transform: scale(1.1);
  }
`;

const CardContent = styled.div`
  padding: 1.5rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 150px;
    height: 150px;
    background: radial-gradient(circle,
      ${props => props.accentColor || '#FF6B6B'}22,
      transparent 70%);
    border-radius: 50%;
    top: -75px;
    right: -75px;
    opacity: 0;
    transition: opacity 0.4s ease;
  }

  ${StoryCard}:hover &::before {
    opacity: 1;
  }
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1rem;
  position: relative;
`;

const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: 2px solid ${props => props.accentColor || '#FF6B6B'};
  box-shadow: 0 0 10px ${props => props.accentColor || '#FF6B6B'}44;
  transition: all 0.3s ease;

  ${StoryCard}:hover & {
    transform: scale(1.1);
    box-shadow: 0 0 15px ${props => props.accentColor || '#FF6B6B'}66;
  }
`;

const UserName = styled.span`
  color: #fff;
  font-weight: 500;
  font-size: 1.1rem;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.3);
`;

const PostTime = styled.span`
  display: block;
  color: rgba(255, 255, 255, 0.6);
  font-size: 0.9rem;
  margin-top: 0.2rem;
`;

const CardTitle = styled.h3`
  font-size: 1.5rem;
  color: #fff;
  margin: 0.5rem 0;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.2);
`;

const CardDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
  margin: 1rem 0;
  position: relative;
`;

const TagContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin: 1rem 0;
`;

const Tag = styled.span`
  background: ${props => `${props.color}11` || '#FF6B6B11'};
  color: ${props => props.color || '#FF6B6B'};
  padding: 0.4rem 1rem;
  border-radius: 20px;
  font-size: 0.9rem;
  border: 1px solid ${props => `${props.color}33` || '#FF6B6B33'};
  transition: all 0.3s ease;

  &:hover {
    background: ${props => `${props.color}22` || '#FF6B6B22'};
    transform: translateY(-2px);
    box-shadow: 0 2px 8px ${props => `${props.color}22` || '#FF6B6B22'};
  }
`;

const CardFooter = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 1rem;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  margin-top: 1rem;
`;

const Stats = styled.div`
  display: flex;
  gap: 1.5rem;
`;

const StatItem = styled.span`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: rgba(255, 255, 255, 0.6);
  font-size: 0.9rem;
  transition: all 0.3s ease;

  span {
    transition: transform 0.3s ease;
  }

  &:hover {
    color: ${props => props.color || '#FF6B6B'};
    
    span {
      transform: scale(1.2);
    }
  }
`;

function Home() {
  const [showRocket, setShowRocket] = useState(false);
  const [showImage, setShowImage] = useState(false);
  const [visibleCards, setVisibleCards] = useState(new Set());
  const [shootingIcons, setShootingIcons] = useState([]);
  const [showStories, setShowStories] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      
      // 当滚动到第一屏的80%时触发火箭动画
      if (scrollPosition > windowHeight * 0.8 && !showRocket) {
        setShowRocket(true);
        // 火箭动画结束后置状态，允许再次触发
        setTimeout(() => setShowRocket(false), 2000);
      }

      // 当滚动到第二屏时显示图片
      if (scrollPosition > windowHeight * 1.2 && !showImage) {
        setShowImage(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [showRocket, showImage]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const cardId = entry.target.dataset.id;
            console.log('Card visible:', cardId); // 添加调试日志
            setVisibleCards(prev => new Set([...prev, cardId]));
          }
        });
      },
      {
        threshold: 0.1, // 降低阈值，使检测更敏感
        rootMargin: '50px' // 添加边距，提前触发检测
      }
    );

    // 延迟观察，确保元素已渲染
    setTimeout(() => {
      const cards = document.querySelectorAll('.story-card');
      console.log('Found cards:', cards.length); // 添加调试日志
      cards.forEach(card => observer.observe(card));
    }, 100);

    return () => observer.disconnect();
  }, []);

  // 修改 handleShoot 函数，添加 event 参数
  const handleShoot = (event, icon, color) => {
    const id = Date.now();
    const buttonRect = event.target.getBoundingClientRect();
    const startX = buttonRect.left + buttonRect.width / 2;
    
    setShootingIcons(prev => [...prev, {
      id,
      icon,
      color,
      startX,
      startY: window.innerHeight - 80
    }]);

    // 动画结束后移除图标
    setTimeout(() => {
      setShootingIcons(prev => prev.filter(item => item.id !== id));
    }, 1000);
  };

  // 底部按钮配置
  const buttons = [
    { icon: '▶️', color: '#FF6B6B', label: 'play' },
    { icon: '❤️', color: '#4ECDC4', label: 'like' },
    { icon: '✨', color: '#FFE66D', label: 'star' },
    { icon: '🔄', color: '#FF6B6B', label: 'refresh' },
    { icon: '💖', color: '#4ECDC4', label: 'love' }
  ];

  // 生成流星并保持状态
  const [meteors, setMeteors] = useState(generateMeteors());

  // 定期更新非永久流星
  useEffect(() => {
    const interval = setInterval(() => {
      setMeteors(prev => {
        const newMeteors = [...prev];
        // 随机替换一些非永久流星
        for (let i = 0; i < newMeteors.length; i++) {
          if (!newMeteors[i].persist && Math.random() < 0.2) { // 20%的概率更新
            newMeteors[i] = {
              color: ['#FF6B6B', '#4ECDC4', '#FFE66D'][Math.floor(Math.random() * 3)],
              top: Math.random() * 60 + 5,
              duration: Math.random() * 6 + 6,
              delay: Math.random() * 3, // 较短的延迟以保持连续性
              width: Math.random() * 100 + 150 + 'px',
              persist: Math.random() < 0.3,
            };
          }
        }
        return newMeteors;
      });
    }, 5000); // 每5秒更新一次

    return () => clearInterval(interval);
  }, []);

  // 添加滚动检测
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const storySection = document.getElementById('story-section');
      
      if (storySection) {
        const sectionTop = storySection.offsetTop;
        const triggerPosition = sectionTop - window.innerHeight / 2;
        
        if (scrollPosition > triggerPosition && !showStories) {
          setShowStories(true);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [showStories]);

  return (
    <>
      <HomeContainer>
        {/* 光束效果 */}
        {[...Array(8)].map((_, i) => (
          <LightBeam key={i} left={i * 15} rotate={i * 5 - 20} />
        ))}
        
        {/* 行星装饰 */}
        <Planet size={100} color="#FF6B6B" top={20} left={15} duration={6} />
        <Planet size={60} color="#4ECDC4" top={70} left={75} duration={8} />
        <Planet size={40} color="#FFE66D" top={30} left={80} duration={5} />
        
        {/* 趣味文案 */}
        <FunText rotate={-15} top={25} left={20}>快来寻找你的宝贝吧~</FunText>
        <FunText rotate={15} top={65} left={70}>错过就很难遇见啦！</FunText>
        <FunText rotate={-5} top={40} left={85}>和Ta贴贴</FunText>

        <Title>奇想寻物</Title>
        <Subtitle>✨ 探索城市中的奇妙物品和故事 ✨</Subtitle>
        
        <ButtonContainer>
          <StyledButton to="/explore" className="primary">
            开始探索
          </StyledButton>
          <StyledButton to="/upload" className="secondary">
            分享故事
          </StyledButton>
        </ButtonContainer>

        {/* 添加浮动元素 */}
        <FloatingElements />
      </HomeContainer>

      {/* 火箭动画 */}
      <Rocket className={showRocket ? 'flying' : ''} />

      {/* 第二屏内容 */}
      <CitySection>
        {/* 渲染流星 */}
        {meteors.map((meteor, index) => (
          <Meteor
            key={`meteor-${index}-${meteor.persist}`}
            color={meteor.color}
            top={meteor.top}
            duration={meteor.duration}
            delay={meteor.delay}
            width={meteor.width}
            persist={meteor.persist}
          />
        ))}

        {/* 霓虹星球 */}
        <NeonPlanet size={100} color="#FF6B6B" top={20} left={15} zIndex={3} />
        <NeonPlanet size={60} color="#4ECDC4" top={40} left={75} delay="1s" zIndex={2} />
        <NeonPlanet size={40} color="#FFE66D" top={60} left={35} delay="2s" zIndex={1} />

        {/* 新的城市剪影 */}
        <CityScapeContainer>
          <Buildings />
          <BuildingLights />
        </CityScapeContainer>

        {/* 渲染发射的图标 */}
        {shootingIcons.map(({ id, icon, color, startX, startY }) => (
          <ShootingIcon
            key={id}
            color={color}
            style={{
              left: startX,
              top: startY
            }}
          >
            {icon}
          </ShootingIcon>
        ))}

        {/* 修改底部按钮栏 */}
        <ButtonBar>
          {buttons.map((button, index) => (
            <IconButton
              key={index}
              color={button.color}
              onClick={(e) => handleShoot(e, button.icon, button.color)}
              aria-label={button.label}
            >
              {button.icon}
            </IconButton>
          ))}
        </ButtonBar>
      </CitySection>

      {/* 修改故事展示部分 */}
      <StoryContainer id="story-section">
        <StoryHeader className={showStories ? 'visible' : ''}>
          <h2>✨ 探索奇妙故事 ✨</h2>
          <p>每个物品背后都有一个等待被发现的故事</p>
        </StoryHeader>

        <StoryGrid className={showStories ? 'visible' : ''}>
          {stories.map((story, index) => (
            <StoryCard
              key={story.id}
              style={{
                transitionDelay: `${index * 0.2}s`
              }}
            >
              <CardHeader>
                <CardImage src={story.image} alt={story.title} />
              </CardHeader>
              <CardContent>
                <UserInfo>
                  <Avatar src={story.user.avatar} accentColor={story.user.color} />
                  <div>
                    <UserName>{story.user.name}</UserName>
                    <PostTime>{story.time}</PostTime>
                  </div>
                </UserInfo>
                <CardTitle>{story.title}</CardTitle>
                <CardDescription>{story.description}</CardDescription>
                <TagContainer>
                  {story.tags.map((tag, i) => (
                    <Tag key={i} color={story.user.color}>{tag}</Tag>
                  ))}
                </TagContainer>
                <CardFooter>
                  <Stats>
                    <StatItem color={story.user.color}>
                      <span>❤️</span> {story.likes}
                    </StatItem>
                    <StatItem color={story.user.color}>
                      <span>💭</span> {story.comments}
                    </StatItem>
                    <StatItem color={story.user.color}>
                      <span>🔄</span> {story.shares}
                    </StatItem>
                  </Stats>
                  <Tag color={story.user.color}>{story.status}</Tag>
                </CardFooter>
              </CardContent>
            </StoryCard>
          ))}
        </StoryGrid>
      </StoryContainer>
    </>
  );
}

export default Home;
