import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';

const float = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #5EEAD4, 0 0 10px #5EEAD4; }
  100% { box-shadow: 0 0 10px #5EEAD4, 0 0 20px #5EEAD4; }
`;

const shimmer = keyframes`
  0% { background-position: -1000px 0; }
  100% { background-position: 1000px 0; }
`;

const UploadContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0F172A 0%, #1E293B 100%);
  color: #fff;
  position: relative;
  overflow: hidden;
  padding: 2rem;

  @media (max-width: 768px) {
    padding: 0;
    background: rgba(30, 41, 59, 0.95);
    backdrop-filter: blur(20px);
  }

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }

  &::after {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, 
      rgba(94, 234, 212, 0.1) 0%,
      transparent 60%
    );
    pointer-events: none;
    z-index: 0;
  }
`;

const UploadContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: rgba(30, 41, 59, 0.8);
  border-radius: 24px;
  padding: 2.5rem;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(94, 234, 212, 0.2);
  box-shadow: 0 0 40px rgba(94, 234, 212, 0.1);
  position: relative;
  z-index: 1;
  width: 100%;
  box-sizing: border-box;

  @media (max-width: 768px) {
    max-width: 100%;
    margin: 0;
    padding: 1rem;
    border-radius: 0;
    border: none;
    background: transparent;
    backdrop-filter: none;
    box-shadow: none;

    &::before {
      display: none;
    }
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 3rem;

  h1 {
    color: #5EEAD4;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(94, 234, 212, 0.3);
    font-weight: 700;

    @media (max-width: 768px) {
      font-size: 2rem;
      margin-bottom: 0.5rem;
    }
  }

  p {
    color: #94A3B8;
    font-size: 1.1rem;

    @media (max-width: 768px) {
      font-size: 0.9rem;
      padding: 0 1rem;
    }
  }
`;

const LoginPrompt = styled.div`
  text-align: center;
  padding: 3rem 2rem;

  h2 {
    color: #5EEAD4;
    font-size: 2rem;
    margin-bottom: 1.5rem;
    text-shadow: 0 0 15px rgba(94, 234, 212, 0.3);
  }

  p {
    color: #94A3B8;
    font-size: 1.1rem;
    margin-bottom: 2rem;
  }
`;

const LoginButton = styled.button`
  padding: 1rem 3rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border: none;
  border-radius: 12px;
  color: #0F172A;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(94, 234, 212, 0.3);
  }
`;

const UploadForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const FormSection = styled.div`
  background: rgba(15, 23, 42, 0.5);
  border-radius: 16px;
  padding: 2rem;
  position: relative;
  z-index: 1;

  @media (max-width: 768px) {
    background: transparent;
    padding: 1rem 0;
    border-radius: 0;

    &::before {
      display: none;
    }
  }
`;

const InputGroup = styled.div`
  position: relative;
  z-index: 2;
  margin-bottom: 1.5rem;
  margin-right: 1rem;

  label {
    display: block;
    color: #94A3B8;
    margin-bottom: 0.5rem;
    font-size: 0.9rem;
  }

  input, textarea, select {
    position: relative;
    z-index: 2;
    width: 95%;
    padding: 0.8rem 1rem;
    background: rgba(15, 23, 42, 0.6);
    border: 1px solid rgba(94, 234, 212, 0.2);
    border-radius: 8px;
    color: #fff;
    font-size: 1rem;
    transition: all 0.3s ease;
    box-sizing: border-box;

    &:focus {
      outline: none;
      border-color: #5EEAD4;
      box-shadow: 0 0 15px rgba(94, 234, 212, 0.2);
    }
  }

  textarea {
    min-height: 120px;
    resize: vertical;
  }

  @media (max-width: 768px) {
    margin-right: 0;
    margin-bottom: 1rem;
    width: 100%;

    input, textarea, select {
      width: 100%;
      padding: 0.8rem;
      background: rgba(15, 23, 42, 0.8);
      border: 1px solid rgba(94, 234, 212, 0.3);
      font-size: 0.9rem;
    }

    textarea {
      min-height: 100px;
    }

    label {
      font-size: 0.8rem;
      margin-bottom: 0.3rem;
    }
  }
`;

const ImageUpload = styled.div`
  border: 2px dashed rgba(94, 234, 212, 0.3);
  border-radius: 12px;
  padding: 2rem;
  text-align: center;
  transition: all 0.3s ease;
  position: relative;

  .preview-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
    gap: 1rem;
    margin-top: 1rem;
  }

  .preview-item {
    position: relative;
    aspect-ratio: 1;

    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
      border-radius: 8px;
      border: 2px solid rgba(94, 234, 212, 0.3);
    }

    .remove-btn {
      position: absolute;
      top: -10px;
      right: -10px;
      background: #FF4B4B;
      color: white;
      border: none;
      border-radius: 50%;
      width: 24px;
      height: 24px;
      cursor: pointer;
      display: flex;
      align-items: center;
      justify-content: center;
      transition: all 0.3s ease;

      &:hover {
        transform: scale(1.1);
        background: #FF3333;
      }
    }
  }
`;

const SubmitButton = styled.button`
  padding: 1rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border: none;
  border-radius: 12px;
  color: #0F172A;
  font-size: 1.2rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 2rem;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(94, 234, 212, 0.3);
  }

  @media print {
    display: none;
  }

  @media (max-width: 768px) {
    &[type="print"] {
      display: none;
    }
  }
`;

const FloatingIcon = styled.div`
  position: absolute;
  font-size: ${props => props.size || '2rem'};
  color: #5EEAD4;
  opacity: 0.5;
  animation: ${float} ${props => props.duration || '3s'} ease-in-out infinite;
  animation-delay: ${props => props.delay || '0s'};
  z-index: 1;

  &::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: inherit;
    filter: blur(8px);
  }
`;

const ProgressIndicator = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 2rem;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 2px;
    background: rgba(94, 234, 212, 0.2);
    transform: translateY(-50%);
    z-index: 0;
  }

  @media (max-width: 768px) {
    margin: 0.5rem 0 1.5rem;
    padding: 0;

    &::before {
      height: 1px;
    }
  }
`;

const ProgressStep = styled.div`
  position: relative;
  z-index: 1;
  background: ${props => props.active ? 
    'linear-gradient(45deg, #5EEAD4, #2DD4BF)' : 
    'rgba(15, 23, 42, 0.8)'};
  color: ${props => props.active ? '#0F172A' : '#5EEAD4'};
  width: 36px;
  height: 36px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  transition: all 0.3s ease;

  &::after {
    content: '${props => props.label}';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    margin-top: 0.5rem;
    white-space: nowrap;
    color: #94A3B8;
    font-size: 0.8rem;
  }

  @media (max-width: 768px) {
    width: 28px;
    height: 28px;
    font-size: 0.8rem;

    &::after {
      font-size: 0.7rem;
      margin-top: 0.3rem;
    }
  }
`;

const ProgressBar = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  width: ${props => props.progress}%;
  height: 2px;
  background: linear-gradient(90deg, #5EEAD4, #2DD4BF);
  transform: translateY(-50%);
  transition: width 0.3s ease;
  z-index: 0;
`;

const AiGenerateButton = styled.button`
  padding: 0.8rem 1.5rem;
  background: ${props => props.disabled ? 
    'rgba(94, 234, 212, 0.2)' : 
    'linear-gradient(45deg, #5EEAD4, #2DD4BF)'};
  border: none;
  border-radius: 8px;
  color: ${props => props.disabled ? '#94A3B8' : '#0F172A'};
  font-size: 1rem;
  font-weight: 600;
  cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;

  &:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(94, 234, 212, 0.3);
  }

  svg {
    width: 20px;
    height: 20px;
  }
`;

const AiInfoText = styled.p`
  color: #94A3B8;
  font-size: 0.9rem;
  margin-top: -0.5rem;
  margin-bottom: 1rem;
  font-style: italic;
`;

// Ê∑ªÂä†Êñ∞ÁöÑÊ†∑ÂºèÁªÑ‰ª∂
const FileUploadButton = styled.div`
  position: relative;
  margin-bottom: 2rem;

  input[type="file"] {
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    opacity: 0;
    cursor: pointer;
  }

  .upload-button {
    padding: 1.5rem;
    background: rgba(15, 23, 42, 0.6);
    border: 2px dashed rgba(94, 234, 212, 0.3);
    border-radius: 12px;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
    transition: all 0.3s ease;

    &:hover {
      border-color: #5EEAD4;
      background: rgba(15, 23, 42, 0.8);
      transform: translateY(-2px);
    }

    .icon {
      font-size: 2.5rem;
      color: #5EEAD4;
      animation: ${float} 3s ease-in-out infinite;
    }

    .text {
      color: #94A3B8;
      font-size: 1rem;
      text-align: center;

      strong {
        color: #5EEAD4;
        display: block;
        margin-bottom: 0.5rem;
      }
    }
  }
`;

const ImagePreviewGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
  gap: 1rem;
  margin-top: 2rem;

  .preview-item {
    position: relative;
    aspect-ratio: 1;
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    border: 2px solid rgba(94, 234, 212, 0.2);
    transition: all 0.3s ease;

    &:hover {
      transform: translateY(-2px);
      border-color: #5EEAD4;
      box-shadow: 0 8px 12px rgba(94, 234, 212, 0.2);

      .remove-btn {
        opacity: 1;
      }
    }

    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
    }

    .remove-btn {
      position: absolute;
      top: 8px;
      right: 8px;
      background: rgba(255, 75, 75, 0.9);
      color: white;
      border: none;
      border-radius: 50%;
      width: 28px;
      height: 28px;
      cursor: pointer;
      display: flex;
      align-items: center;
      justify-content: center;
      transition: all 0.3s ease;
      opacity: 0;
      font-size: 1.2rem;

      &:hover {
        transform: scale(1.1);
        background: #FF3333;
      }
    }
  }

  .image-count {
    position: absolute;
    bottom: 8px;
    left: 8px;
    background: rgba(15, 23, 42, 0.8);
    color: #5EEAD4;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 0.8rem;
  }

  @media (max-width: 768px) {
    grid-template-columns: repeat(auto-fill, minmax(90px, 1fr));
    gap: 0.5rem;
    margin-top: 1rem;

    .preview-item {
      border-radius: 8px;

      .remove-btn {
        opacity: 1;
        width: 24px;
        height: 24px;
      }
    }
  }
`;

function Upload() {
  const [formData, setFormData] = useState({
    name: '',
    rarity: 'N',
    description: '',
    location: '',
    clue: '',
    images: [],
    password: '',
    hiddenInfo: '',
    story: '',
    discoveryStory: '',
    additionalInfo: '',
    secretContent: {
      title: '',
      additionalInfo: '',
      discoveryStory: '',
      secretLocations: ['', '', '']
    }
  });

  const navigate = useNavigate();

  // Ê∑ªÂä†ÂΩìÂâçÊ≠•È™§ state
  const [currentStep, setCurrentStep] = useState(1);

  // Ê∑ªÂä†Ë°®ÂçïÈ™åËØÅÁä∂ÊÄÅ
  const [formValidation, setFormValidation] = useState({
    basicInfo: false,
    locationInfo: false,
    imageInfo: false,
    storyInfo: false,
    secretInfo: false
  });

  // Ê∑ªÂä† AI ÁîüÊàêÁä∂ÊÄÅ
  const [isGenerating, setIsGenerating] = useState(false);

  // Ê£ÄÊü•ÊòØÂê¶ÂèØ‰ª•‰ΩøÁî® AI ÁîüÊàê
  const canUseAiGenerate = () => {
    return formData.name && 
           formData.description && 
           formData.images.length > 0 && 
           formData.password && 
           formData.secretContent.title;
  };

  // AI ÁîüÊàêÂ§ÑÁêÜÂáΩÊï∞
  const handleAiGenerate = async () => {
    if (!canUseAiGenerate()) return;

    setIsGenerating(true);
    try {
      // ËøôÈáåÊ∑ªÂä†ÂÆûÈôÖÁöÑ AI ÁîüÊàêÈÄªËæë
      // const response = await generateAiStory(formData);
      // setFormData(prev => ({
      //   ...prev,
      //   story: response.story,
      //   discoveryStory: response.discoveryStory,
      //   additionalInfo: response.additionalInfo
      // }));

      // Ê®°Êãü AI ÁîüÊàêÂª∂Ëøü
      await new Promise(resolve => setTimeout(resolve, 2000));

    } catch (error) {
      console.error('AI ÁîüÊàêÂ§±Ë¥•:', error);
    } finally {
      setIsGenerating(false);
    }
  };

  // Ê£ÄÊü•Âü∫Êú¨‰ø°ÊÅØÊòØÂê¶ÂÆåÊï¥
  const validateBasicInfo = () => {
    const isValid = 
      formData.name.length > 0 && 
      formData.rarity !== '' && 
      formData.description.length > 0;

    setFormValidation(prev => ({
      ...prev,
      basicInfo: isValid
    }));

    if (isValid && currentStep === 1) {
      setCurrentStep(2);
    }
  };

  // Ê£ÄÊü•‰ΩçÁΩÆ‰ø°ÊÅØÊòØÂê¶ÂÆåÊï¥
  const validateLocationInfo = () => {
    const isValid = 
      formData.location !== '' && 
      formData.clue.length > 0;

    setFormValidation(prev => ({
      ...prev,
      locationInfo: isValid
    }));

    if (isValid && currentStep === 2) {
      setCurrentStep(3);
    }
  };

  // Ê£ÄÊü•ÂõæÁâáÊòØÂê¶‰∏ä‰º†
  const validateImageInfo = () => {
    const isValid = formData.images.length > 0;
    setFormValidation(prev => ({
      ...prev,
      imageInfo: isValid
    }));

    if (isValid && currentStep === 3) {
      setCurrentStep(4);
    }
  };

  // Ê∑ªÂä†ÊïÖ‰∫ãÊÅØÈ™åËØÅ
  const validateStoryInfo = () => {
    const isValid = 
      formData.story.length > 0 && 
      formData.discoveryStory.length > 0 &&
      formData.additionalInfo.length > 0;

    setFormValidation(prev => ({
      ...prev,
      storyInfo: isValid
    }));

    if (isValid && currentStep === 4) {
      setCurrentStep(5);
    }
  };

  // ‰øÆÊîπÁ•ûÁßòÂÜÖÂÆπÈ™åËØÅ
  const validateSecretInfo = () => {
    const { secretContent } = formData;
    const isValid = 
      formData.password.length > 0 && 
      secretContent.title.length > 0 &&
      secretContent.additionalInfo.length > 0 &&
      secretContent.discoveryStory.length > 0 &&
      secretContent.secretLocations.some(location => location.length > 0);

    setFormValidation(prev => ({
      ...prev,
      secretInfo: isValid
    }));
  };

  // ‰øÆÊîπËæìÂÖ•Â§ÑÁêÜÂáΩÊï∞
  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name.startsWith('secretContent.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        secretContent: {
          ...prev.secretContent,
          [field]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }

    // Ê†πÊçÆËæìÂÖ•Â≠óÊÆµÈ™åËØÅÁõ∏Â∫îÈÉ®ÂàÜ
    switch(name) {
      case 'name':
      case 'rarity':
      case 'description':
        setTimeout(validateBasicInfo, 300);
        break;
      case 'location':
      case 'clue':
        setTimeout(validateLocationInfo, 300);
