import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

// 动画效果
const float = keyframes`
  0% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
  100% { transform: translateY(0px) rotate(0deg); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #FF6B6B, 0 0 20px #FF6B6B; }
  100% { box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #FF6B6B, 0 0 40px #FF6B6B; }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const rocketFly = keyframes`
  0% {
    transform: translateY(100vh) rotate(-45deg);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateY(-100vh) rotate(-45deg);
    opacity: 0;
  }
`;

const fadeInScale = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.8);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
`;

const HomeContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  padding: 2rem;
  background: #1a1a1a;
  color: #fff;
`;

const LightBeam = styled.div`
  position: absolute;
  width: 2px;
  height: 100%;
  background: linear-gradient(to bottom, transparent, #FF6B6B, transparent);
  opacity: 0.5;
  transform-origin: 50% 0;
  animation: ${float} 3s ease-in-out infinite;
  left: ${props => props.left}%;
  transform: rotate(${props => props.rotate}deg);
`;

const Planet = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: ${props => props.color};
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  animation: ${float} ${props => props.duration}s ease-in-out infinite;
  z-index: 1;
  box-shadow: 0 0 20px rgba(255,255,255,0.2);

  &::after {
    content: '';
    position: absolute;
    width: 110%;
    height: 110%;
    border-radius: 50%;
    border: 2px solid rgba(255,255,255,0.2);
    left: -5%;
    top: -5%;
    animation: ${pulse} 4s ease-in-out infinite;
  }
`;

const Title = styled.h1`
  font-size: 4.5rem;
  font-weight: bold;
  color: #fff;
  margin-bottom: 1rem;
  text-align: center;
  position: relative;
  z-index: 2;
  text-shadow: 0 0 10px rgba(255,107,107,0.5);
  animation: ${glow} 2s ease-in-out infinite alternate;
`;

const Subtitle = styled.p`
  font-size: 1.8rem;
  color: #FF6B6B;
  margin-bottom: 2rem;
  text-align: center;
  font-family: 'Noto Sans SC', sans-serif;
  position: relative;
  z-index: 2;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 2rem;
  margin-top: 2rem;
  position: relative;
  z-index: 2;
`;

const StyledButton = styled(Link)`
  padding: 1.2rem 2.5rem;
  font-size: 1.2rem;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      120deg,
      transparent,
      rgba(255, 255, 255, 0.2),
      transparent
    );
    transition: 0.5s;
  }

  &:hover::before {
    left: 100%;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 20px rgba(255,107,107,0.4);
  }

  &.primary {
    background: #FF6B6B;
    color: white;
  }

  &.secondary {
    background: transparent;
    border: 2px solid #4ECDC4;
    color: #4ECDC4;
  }
`;

const FunText = styled.span`
  position: absolute;
  font-size: 1rem;
  color: #FFE66D;
  transform: rotate(${props => props.rotate}deg);
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  z-index: 2;
`;

const Rocket = styled.div`
  position: fixed;
  width: 60px;
  height: 60px;
  right: 10%;
  z-index: 10;
  opacity: 0;
  pointer-events: none;
  
  &.flying {
    animation: ${rocketFly} 2s ease-in-out forwards;
  }

  &::before {
    content: '🚀';
    font-size: 40px;
    position: absolute;
    transform: rotate(45deg);
  }

  &::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 100px;
    background: linear-gradient(to bottom, #FF6B6B, transparent);
    transform: translateY(20px) rotate(45deg);
    filter: blur(10px);
    opacity: 0.5;
  }
`;

const ImageFrame = styled.div`
  position: relative;
  width: 300px;
  height: 200px;
  margin: 3rem auto;
  opacity: 0;
  transform: scale(0.8);

  &.visible {
    animation: ${fadeInScale} 1s ease-out forwards;
  }

  &::before {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    right: -20px;
    bottom: -20px;
    border: 2px solid #FF6B6B;
    border-radius: 10px;
    background: rgba(40, 40, 40, 0.8);
    backdrop-filter: blur(10px);
    z-index: -1;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 5px;
  }
`;

const ScrollSection = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  padding: 2rem;
`;

function Home() {
  const [showRocket, setShowRocket] = useState(false);
  const [showImage, setShowImage] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      
      // 当滚动到第一屏的80%时触发火箭动画
      if (scrollPosition > windowHeight * 0.8 && !showRocket) {
        setShowRocket(true);
        // 火箭动画结束后重置状态，允许再次触发
        setTimeout(() => setShowRocket(false), 2000);
      }

      // 当滚动到第二屏时显示图片
      if (scrollPosition > windowHeight * 1.2 && !showImage) {
        setShowImage(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [showRocket, showImage]);

  return (
    <>
      <HomeContainer>
        {/* 光束效果 */}
        {[...Array(8)].map((_, i) => (
          <LightBeam key={i} left={i * 15} rotate={i * 5 - 20} />
        ))}
        
        {/* 行星装饰 */}
        <Planet size={100} color="#FF6B6B" top={20} left={15} duration={6} />
        <Planet size={60} color="#4ECDC4" top={70} left={75} duration={8} />
        <Planet size={40} color="#FFE66D" top={30} left={80} duration={5} />
        
        {/* 趣味文案 */}
        <FunText rotate={-15} top={25} left={20}>快来寻找你的宝贝吧~</FunText>
        <FunText rotate={15} top={65} left={70}>错过就很难遇见啦！</FunText>
        <FunText rotate={-5} top={40} left={85}>和Ta贴贴</FunText>

        <Title>奇想寻物</Title>
        <Subtitle>✨ 探索城市中的奇妙物品和故事 ✨</Subtitle>
        
        <ButtonContainer>
          <StyledButton to="/explore" className="primary">
            开始探索
          </StyledButton>
          <StyledButton to="/upload" className="secondary">
            分享故事
          </StyledButton>
        </ButtonContainer>
      </HomeContainer>

      {/* 火箭动画 */}
      <Rocket className={showRocket ? 'flying' : ''} />

      {/* 第二屏内容 */}
      <ScrollSection>
        <ImageFrame className={showImage ? 'visible' : ''}>
          <img 
            src="https://images.unsplash.com/photo-1516796181074-bf453fbfa3e6?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2340&q=80" 
            alt="探索图片"
          />
        </ImageFrame>
      </ScrollSection>
    </>
  );
}

export default Home;
