import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';

const float = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #5EEAD4, 0 0 10px #5EEAD4; }
  100% { box-shadow: 0 0 10px #5EEAD4, 0 0 20px #5EEAD4; }
`;

const shimmer = keyframes`
  0% { background-position: -1000px 0; }
  100% { background-position: 1000px 0; }
`;

const UploadContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0F172A 0%, #1E293B 100%);
  color: #fff;
  padding: 2rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }

  &::after {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, 
      rgba(94, 234, 212, 0.1) 0%,
      transparent 60%
    );
    pointer-events: none;
    z-index: 0;
  }
`;

const UploadContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: rgba(30, 41, 59, 0.8);
  border-radius: 24px;
  padding: 2.5rem;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(94, 234, 212, 0.2);
  box-shadow: 0 0 40px rgba(94, 234, 212, 0.1);
  position: relative;
  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    inset: -2px;
    border-radius: 25px;
    padding: 2px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    z-index: -1;
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 3rem;

  h1 {
    color: #5EEAD4;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(94, 234, 212, 0.3);
    font-weight: 700;
  }

  p {
    color: #94A3B8;
    font-size: 1.1rem;
  }
`;

const LoginPrompt = styled.div`
  text-align: center;
  padding: 3rem 2rem;

  h2 {
    color: #5EEAD4;
    font-size: 2rem;
    margin-bottom: 1.5rem;
    text-shadow: 0 0 15px rgba(94, 234, 212, 0.3);
  }

  p {
    color: #94A3B8;
    font-size: 1.1rem;
    margin-bottom: 2rem;
  }
`;

const LoginButton = styled.button`
  padding: 1rem 3rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border: none;
  border-radius: 12px;
  color: #0F172A;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(94, 234, 212, 0.3);
  }
`;

const UploadForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const FormSection = styled.div`
  background: rgba(15, 23, 42, 0.5);
  border-radius: 16px;
  padding: 2rem;
  position: relative;
  z-index: 1;
  transform: translateY(20px);
  opacity: 0;
  animation: fadeIn 0.6s ease-out forwards;
  animation-delay: ${props => props.delay || '0s'};

  @keyframes fadeIn {
    to {
      transform: translateY(0);
      opacity: 1;
    }
  }

  &:hover {
    transform: translateY(-2px);
    transition: transform 0.3s ease;
  }

  & > * {
    max-width: 95%;
  }

  &::before {
    content: '';
    position: absolute;
    inset: -1px;
    border-radius: 16px;
    padding: 1px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    z-index: -1;
  }

  h3 {
    color: #5EEAD4;
    margin-bottom: 1.5rem;
    font-size: 1.3rem;
  }
`;

const InputGroup = styled.div`
  position: relative;
  z-index: 2;
  margin-bottom: 1.5rem;
  margin-right: 1rem;

  label {
    display: block;
    color: #94A3B8;
    margin-bottom: 0.5rem;
    font-size: 0.9rem;
  }

  input, textarea, select {
    position: relative;
    z-index: 2;
    width: 95%;
    padding: 0.8rem 1rem;
    background: rgba(15, 23, 42, 0.6);
    border: 1px solid rgba(94, 234, 212, 0.2);
    border-radius: 8px;
    color: #fff;
    font-size: 1rem;
    transition: all 0.3s ease;

    &:focus {
      outline: none;
      border-color: #5EEAD4;
      box-shadow: 0 0 15px rgba(94, 234, 212, 0.2);
    }
  }

  textarea {
    min-height: 120px;
    resize: vertical;
  }
`;

const ImageUpload = styled.div`
  border: 2px dashed rgba(94, 234, 212, 0.3);
  border-radius: 12px;
  padding: 2rem;
  text-align: center;
  transition: all 0.3s ease;
  position: relative;

  &:hover {
    border-color: #5EEAD4;
    background: rgba(94, 234, 212, 0.05);
  }

  input[type="file"] {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    cursor: pointer;
  }

  label {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    cursor: pointer;

    span {
      font-size: 2rem;
      margin-bottom: 0.5rem;
    }
  }

  p {
    color: #94A3B8;
    margin-top: 1rem;
  }

  .preview-container {
    margin-top: 1rem;
    position: relative;

    img {
      max-width: 100%;
      max-height: 200px;
      border-radius: 8px;
      border: 2px solid rgba(94, 234, 212, 0.3);
    }

    .remove-btn {
      position: absolute;
      top: -10px;
      right: -10px;
      background: #FF4B4B;
      color: white;
      border: none;
      border-radius: 50%;
      width: 24px;
      height: 24px;
      cursor: pointer;
      display: flex;
      align-items: center;
      justify-content: center;
      transition: all 0.3s ease;

      &:hover {
        transform: scale(1.1);
        background: #FF3333;
      }
    }
  }
`;

const SubmitButton = styled.button`
  padding: 1rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border: none;
  border-radius: 12px;
  color: #0F172A;
  font-size: 1.2rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 2rem;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(94, 234, 212, 0.3);
  }
`;

const FloatingIcon = styled.div`
  position: absolute;
  font-size: ${props => props.size || '2rem'};
  color: #5EEAD4;
  opacity: 0.5;
  animation: ${float} ${props => props.duration || '3s'} ease-in-out infinite;
  animation-delay: ${props => props.delay || '0s'};
  z-index: 1;

  &::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: inherit;
    filter: blur(8px);
  }
`;

const ProgressIndicator = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 2rem;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 2px;
    background: rgba(94, 234, 212, 0.2);
    transform: translateY(-50%);
    z-index: 0;
  }
`;

const ProgressStep = styled.div`
  position: relative;
  z-index: 1;
  background: ${props => props.active ? 
    'linear-gradient(45deg, #5EEAD4, #2DD4BF)' : 
    'rgba(15, 23, 42, 0.8)'};
  color: ${props => props.active ? '#0F172A' : '#5EEAD4'};
  width: 36px;
  height: 36px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  transition: all 0.3s ease;

  &::after {
    content: '${props => props.label}';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    margin-top: 0.5rem;
    white-space: nowrap;
    color: #94A3B8;
    font-size: 0.8rem;
  }
`;

function Upload() {
  const [formData, setFormData] = useState({
    name: '',
    rarity: 'N',
    description: '',
    location: '',
    clue: '',
    image: null,
    password: '',
    hiddenInfo: ''
  });

  const navigate = useNavigate();

  // Ê∑ªÂä†ÂΩìÂâçÊ≠•È™§ state
  const [currentStep, setCurrentStep] = useState(1);

  // Ê∑ªÂä†È¢ÑËßàÂõæÁâá URL state
  const [previewUrl, setPreviewUrl] = useState(null);

  // Â§ÑÁêÜËæìÂÖ•ÂèòÂåñ
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    console.log(`${name} changed to:`, value); // Ë∞ÉËØïÊó•Âøó
  };

  // ‰øÆÊîπÂõæÁâá‰∏ä‰º†Â§ÑÁêÜÂáΩÊï∞
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, image: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Ê∑ªÂä†ÁßªÈô§ÂõæÁâáÂáΩÊï∞
  const handleRemoveImage = () => {
    setFormData(prev => ({ ...prev, image: null }));
    setPreviewUrl(null);
  };

  // Â§ÑÁêÜË°®ÂçïÊèê‰∫§
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Ë°®ÂçïÊèê‰∫§ÁöÑÊï∞ÊçÆÂ¶Ç‰∏ã:');
    console.log('ÂõæÁâá:', formData.image);
    console.log('ÂØÜÁ†Å:', formData.password);
    console.log('ÈöêËóè‰ø°ÊÅØ:', formData.hiddenInfo);
    // Ê∑ªÂä†Êèê‰∫§ÈÄªËæë
    // ‰æãÂ¶ÇÔºåÂèëÈÄÅÊï∞ÊçÆÂà∞ÊúçÂä°Âô®
    // Â§ÑÁêÜÂìçÂ∫î 

    // Ë∑≥ËΩ¨Âà∞ÂÆåÊàêÈ°µ
    navigate('/upload-complete');
  };

  return (
    <UploadContainer>
      <FloatingIcon style={{ top: '10%', left: '10%' }} delay="0s">üéØ</FloatingIcon>
      <FloatingIcon style={{ top: '20%', right: '15%' }} delay="0.5s" size="2.5rem">‚ú®</FloatingIcon>
      <FloatingIcon style={{ bottom: '15%', left: '20%' }} delay="1s">üîç</FloatingIcon>

      <UploadContent>
        <Header>
          <h1>‰∏ä‰º†Áâ©ÂìÅ</h1>
          <p>ÂàÜ‰∫´‰Ω†ÁöÑÂèëÁé∞ÔºåËÆ©Êõ¥Â§ö‰∫∫ÂèÇ‰∏éÂØªÂÆùÁöÑ‰πêË∂£</p>
        </Header>

        <ProgressIndicator>
          <ProgressStep active={currentStep >= 1} label="Âü∫Êú¨‰ø°ÊÅØ">1</ProgressStep>
          <ProgressStep active={currentStep >= 2} label="‰ΩçÁΩÆ‰ø°ÊÅØ">2</ProgressStep>
          <ProgressStep active={currentStep >= 3} label="ÂõæÁâá‰∏ä‰º†">3</ProgressStep>
          <ProgressStep active={currentStep >= 4} label="Á•ûÁßòÂÜÖÂÆπ">4</ProgressStep>
        </ProgressIndicator>

        <UploadForm onSubmit={handleSubmit}>
          <FormSection delay="0.2s">
            <h3>Âü∫Êú¨‰ø°ÊÅØ</h3>
            <InputGroup>
              <label>Áâ©ÂìÅÂêçÁß∞</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Áªô‰Ω†ÁöÑÁâ©ÂìÅËµ∑‰∏™Áã¨ÁâπÁöÑÂêçÂ≠ó"
              />
            </InputGroup>

            <InputGroup>
              <label>Á®ÄÊúâÂ∫¶</label>
              <select
                name="rarity"
                value={formData.rarity}
                onChange={handleInputChange}
              >
                <option value="N">N - ÊôÆÈÄö</option>
                <option value="R">R - Á®ÄÊúâ</option>
                <option value="SR">SR - Ë∂ÖÁ®ÄÊúâ</option>
                <option value="SSR">SSR - Áªù‰∏ñÁèçÂìÅ</option>
              </select>
            </InputGroup>

            <InputGroup>
              <label>Áâ©ÂìÅÊèèËø∞</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="ËØ¶ÁªÜÊèèËø∞‰∏Ä‰∏ãËøô‰∏™Áâ©ÂìÅÁöÑÁâπÁÇπ..."
              />
            </InputGroup>
          </FormSection>

          <FormSection delay="0.4s">
            <h3>‰ΩçÁΩÆ‰ø°ÊÅØ</h3>
            <InputGroup>
              <label>ËóèÂåøÂú∞ÁÇπ</label>
              <select
                name="location"
                value={formData.location}
                onChange={handleInputChange}
              >
                <option value="A">A - Â§©Âè∞Âå∫Âüü3Ê•º</option>
                <option value="B">B - ÂâçÈó®Âå∫Âüü</option>
                <option value="C">C - Áü•ËØÜËä±Âõ≠</option>
                <option value="D">D - ÂàõÊÑèÂÆûÈ™åÂÆ§</option>

              </select>
            </InputGroup>

            <InputGroup>
              <label>ÂØªÂÆùÁ∫øÁ¥¢</label>
              <textarea
                name="clue"
                value={formData.clue}
                onChange={handleInputChange}
                placeholder="ÁªôÂØªÂÆùËÄÖ‰∏Ä‰∫õÊèêÁ§∫..."
              />
            </InputGroup>
          </FormSection>

          <FormSection delay="0.6s">
            <h3>ÂõæÁâá‰∏ä‰º†</h3>
            <ImageUpload>
              <input
                type="file"
                accept="image/*"
                id="imageUpload"
                onChange={handleImageUpload}
              />
              <label>
                <span role="img" aria-label="upload">üì∏</span>
                <p>ÁÇπÂáªÊàñÊãñÊãΩ‰∏ä‰º†ÂõæÁâá</p>
              </label>

              {previewUrl && (
                <div className="preview-container">
                  <img src={previewUrl} alt="È¢ÑËßà" />
                  <button 
                    className="remove-btn"
                    onClick={handleRemoveImage}
                    type="button"
                  >
                    √ó
                  </button>
                </div>
              )}
            </ImageUpload>
          </FormSection>

          <FormSection delay="0.8s">
            <h3>Á•ûÁßòÂÜÖÂÆπ</h3>
            <InputGroup>
              <label>È™åËØÅÂØÜÁ†Å</label>
              <input
                type="text"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="ËÆæÁΩÆ‰∏Ä‰∏™ÂØÜÁ†ÅÔºåÁî®‰∫éËß£ÈîÅÈöêËóèÂÜÖÂÆπ"
              />
            </InputGroup>

            <InputGroup>
              <label>ÈöêËóè‰ø°ÊÅØ</label>
              <textarea
                name="hiddenInfo"
                value={formData.hiddenInfo}
                onChange={handleInputChange}
                placeholder="Ëøô‰∫õ‰ø°ÊÅØÂè™ÊúâÈÄöËøáÈ™åËØÅÂêéÊâçËÉΩÁúãÂà∞..."
              />
            </InputGroup>
          </FormSection>

          <SubmitButton type="submit">
            ÂèëÂ∏ÉÁâ©ÂìÅ <span role="img" aria-label="rocket">üöÄ</span>
          </SubmitButton>
        </UploadForm>
      </UploadContent>
    </UploadContainer>
  );
}

export default Upload;
