import styled from 'styled-components';
import Navigation from './Navigation';

const MainContainer = styled.div`
  min-height: 100vh;
  background: #1a1a1a;
  color: #fff;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, rgba(40, 40, 40, 0.8), #1a1a1a);
    pointer-events: none;
    z-index: 1;
  }
`;

const Content = styled.main`
  position: relative;
  z-index: 2;
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const StarBackground = styled.div`
  position: fixed;
  width: 100%;
  height: 100%;
  background: ${props => `radial-gradient(2px 2px at ${props.left}px ${props.top}px, 
    rgba(255, 255, 255, 0.5), rgba(0, 0, 0, 0))`};
  animation: twinkle 1.5s ease-in-out infinite alternate;
  
  @keyframes twinkle {
    from { opacity: 0.3; }
    to { opacity: 1; }
  }
`;

function Layout({ children }) {
  // 创建星星背景
  const stars = Array.from({ length: 50 }, (_, i) => ({
    left: Math.random() * window.innerWidth,
    top: Math.random() * window.innerHeight,
  }));

  return (
    <MainContainer>
      {stars.map((star, i) => (
        <StarBackground key={i} {...star} />
      ))}
      <Navigation />
      <Content>{children}</Content>
    </MainContainer>
  );
}

export default Layout;
