import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

// Âä®ÁîªÊïàÊûú
const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
  100% { transform: translateY(0px); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #4ECDC4, 0 0 10px #4ECDC4; }
  100% { box-shadow: 0 0 10px #4ECDC4, 0 0 20px #4ECDC4; }
`;

// Ê∑ªÂä†Êñ∞ÁöÑÂä®ÁîªÊïàÊûú
const shimmer = keyframes`
  0% {
    background-position: -1000px 0;
  }
  100% {
    background-position: 1000px 0;
  }
`;

const gradientBg = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const ExploreContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(
    135deg,
    #1a1a1a 0%,
    #2a2a2a 50%,
    #1a1a1a 100%
  );
  background-size: 400% 400%;
  animation: ${gradientBg} 15s ease infinite;
  color: #fff;
  padding: 2rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(
      circle at 50% 0,
      rgba(78, 205, 196, 0.1),
      transparent 50%
    );
    pointer-events: none;
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;
  z-index: 2;
`;

const Title = styled.h1`
  font-size: 3rem;
  color: #4ECDC4;
  margin-bottom: 1rem;
  position: relative;
  text-shadow: 
    0 0 10px rgba(78, 205, 196, 0.5),
    0 0 20px rgba(78, 205, 196, 0.3),
    0 0 30px rgba(78, 205, 196, 0.2);
  animation: ${glow} 2s ease-in-out infinite alternate;

  &::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    width: 150px;
    height: 2px;
    background: linear-gradient(
      90deg,
      transparent,
      #4ECDC4,
      transparent
    );
    animation: ${shimmer} 2s infinite;
  }

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const SearchBar = styled.div`
  max-width: 600px;
  margin: 0 auto;
  position: relative;

  input {
    width: 100%;
    padding: 1rem 2rem;
    border-radius: 50px;
    border: 2px solid #4ECDC4;
    background: rgba(78, 205, 196, 0.1);
    color: #fff;
    font-size: 1.1rem;
    backdrop-filter: blur(5px);

    &:focus {
      outline: none;
      box-shadow: 0 0 15px rgba(78, 205, 196, 0.3);
    }
  }
`;

const SearchResults = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  margin-top: 0.5rem;
  background: rgba(30, 30, 30, 0.95);
  border-radius: 15px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(78, 205, 196, 0.2);
  max-height: 300px;
  overflow-y: auto;
  z-index: 10;
  opacity: ${props => props.show ? 1 : 0};
  transform: translateY(${props => props.show ? '0' : '-10px'});
  pointer-events: ${props => props.show ? 'all' : 'none'};
  transition: all 0.3s ease;

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb {
    background: #4ECDC4;
    border-radius: 4px;
  }
`;

const SearchResultItem = styled.div`
  padding: 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(78, 205, 196, 0.1);
  }

  img {
    width: 50px;
    height: 50px;
    border-radius: 8px;
    object-fit: cover;
  }

  .info {
    flex: 1;

    h4 {
      color: #fff;
      margin: 0 0 0.3rem 0;
    }

    p {
      color: rgba(255, 255, 255, 0.6);
      font-size: 0.9rem;
      margin: 0;
    }
  }
`;

const FilterSection = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin: 2rem 0;
  flex-wrap: wrap;
`;

const FilterTag = styled.button`
  padding: 0.5rem 1.5rem;
  border-radius: 20px;
  border: 1px solid #4ECDC4;
  background: ${props => props.active ? '#4ECDC4' : 'transparent'};
  color: ${props => props.active ? '#1a1a1a' : '#4ECDC4'};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(78, 205, 196, 0.2);
  }
`;

const ItemGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 2rem;
  padding: 2rem 0;
`;

const ItemCard = styled.div`
  background: rgba(30, 30, 30, 0.8);
  border-radius: 15px;
  overflow: hidden;
  transition: all 0.3s ease;
  animation: ${float} 6s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 20px rgba(78, 205, 196, 0.2);
  }
`;

const ItemImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const ItemInfo = styled.div`
  padding: 1.5rem;
`;

const ItemTitle = styled.h3`
  font-size: 1.2rem;
  color: #4ECDC4;
  margin-bottom: 0.5rem;
`;

const ItemDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  font-size: 0.9rem;
  margin-bottom: 1rem;
`;

const ItemFooter = styled.div`
  display: flex;
  justify-content: space-between;
