import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

// 动画效果
const float = keyframes`
  0% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
  100% { transform: translateY(0px) rotate(0deg); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #FF6B6B, 0 0 20px #FF6B6B; }
  100% { box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #FF6B6B, 0 0 40px #FF6B6B; }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const rocketFly = keyframes`
  0% {
    transform: translateY(100vh) rotate(-45deg);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateY(-100vh) rotate(-45deg);
    opacity: 0;
  }
`;

const fadeInScale = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.8);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
`;

const HomeContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  padding: 2rem;
  background: #1a1a1a;
  color: #fff;
`;

const LightBeam = styled.div`
  position: absolute;
  width: 2px;
  height: 100%;
  background: linear-gradient(to bottom, transparent, #FF6B6B, transparent);
  opacity: 0.5;
  transform-origin: 50% 0;
  animation: ${float} 3s ease-in-out infinite;
  left: ${props => props.left}%;
  transform: rotate(${props => props.rotate}deg);
`;

const Planet = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: ${props => props.color};
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  animation: ${float} ${props => props.duration}s ease-in-out infinite;
  z-index: 1;
  box-shadow: 0 0 20px rgba(255,255,255,0.2);

  &::after {
    content: '';
    position: absolute;
    width: 110%;
    height: 110%;
    border-radius: 50%;
    border: 2px solid rgba(255,255,255,0.2);
    left: -5%;
    top: -5%;
    animation: ${pulse} 4s ease-in-out infinite;
  }
`;

const Title = styled.h1`
  font-size: 4.5rem;
  font-weight: bold;
  color: #fff;
  margin-bottom: 1rem;
  text-align: center;
  position: relative;
  z-index: 2;
  text-shadow: 0 0 10px rgba(255,107,107,0.5);
  animation: ${glow} 2s ease-in-out infinite alternate;
`;

const Subtitle = styled.p`
  font-size: 1.8rem;
  color: #FF6B6B;
  margin-bottom: 2rem;
  text-align: center;
  font-family: 'Noto Sans SC', sans-serif;
  position: relative;
  z-index: 2;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 2rem;
  margin-top: 2rem;
  position: relative;
  z-index: 2;
`;

const StyledButton = styled(Link)`
  padding: 1.2rem 2.5rem;
  font-size: 1.2rem;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
