function Home() {
  const [showRocket, setShowRocket] = useState(false);
  const [showImage, setShowImage] = useState(false);
  const [visibleCards, setVisibleCards] = useState(new Set());

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      
      // 当滚动到第一屏的80%时触发火箭动画
      if (scrollPosition > windowHeight * 0.8 && !showRocket) {
        setShowRocket(true);
        // 火箭动画结束后重置状态，允许再次触发
        setTimeout(() => setShowRocket(false), 2000);
      }

      // 当滚动到第二屏时显示图片
      if (scrollPosition > windowHeight * 1.2 && !showImage) {
        setShowImage(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [showRocket, showImage]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            setVisibleCards(prev => new Set([...prev, entry.target.dataset.id]));
          }
        });
      },
      {
        threshold: 0.2
      }
    );

    document.querySelectorAll('.story-card').forEach(card => {
      observer.observe(card);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <>
      <HomeContainer>
        {/* 光束效果 */}
        {[...Array(8)].map((_, i) => (
          <LightBeam key={i} left={i * 15} rotate={i * 5 - 20} />
        ))}
        
        {/* 行星装饰 */}
        <Planet size={100} color="#FF6B6B" top={20} left={15} duration={6} />
        <Planet size={60} color="#4ECDC4" top={70} left={75} duration={8} />
        <Planet size={40} color="#FFE66D" top={30} left={80} duration={5} />
        
        {/* 趣味文案 */}
        <FunText rotate={-15} top={25} left={20}>快来寻找你的宝贝吧~</FunText>
        <FunText rotate={15} top={65} left={70}>错过就很难遇见啦！</FunText>
        <FunText rotate={-5} top={40} left={85}>和Ta贴贴</FunText>

        <Title>奇想寻物</Title>
        <Subtitle>✨ 探索城市中的奇妙物品和故事 ✨</Subtitle>
        
