import React, { useEffect, useState, useRef, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';

const DetailContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0F172A 0%, #1E293B 100%);
  color: #fff;
  padding: 1rem;
  position: relative;
  overflow: hidden;

  @media (max-width: 768px) {
    padding: 0.5rem;
  }

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }
`;

const PrintableContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: rgba(30, 41, 59, 0.8);
  border-radius: 24px;
  padding: 1.5rem;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(94, 234, 212, 0.2);
  box-shadow: 0 0 40px rgba(94, 234, 212, 0.1);
  position: relative;

  @media (max-width: 768px) {
    padding: 1rem;
    border-radius: 16px;
    margin: 0;
    width: 100%;
    overflow: hidden;
  }
`;

const ItemHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;

  h1 {
    color: #5EEAD4;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(94, 234, 212, 0.3);
    font-weight: 700;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }

  .rarity-badge {
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    padding: 0.5rem 1.5rem;
    border-radius: 20px;
    font-weight: 600;
    display: inline-block;
    transform: translateY(-5px);
    animation: float 3s ease-in-out infinite;
  }
`;

const ItemImage = styled.div`
  position: relative;
  margin-bottom: 3rem;

  img {
    width: 100%;
    height: 400px;
    object-fit: cover;
    border-radius: 16px;
    border: 2px solid rgba(94, 234, 212, 0.3);
  }

  &::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    background: linear-gradient(180deg, transparent 0%, rgba(15, 23, 42, 0.2) 100%);
  }
`;

const ItemSection = styled.section`
  margin-bottom: 2.5rem;
  opacity: 0;
  transform: translateY(20px);

  &.visible {
    animation: fadeIn 0.8s ease-out forwards;
  }

  &.map-section {
    .map-container {
      position: relative;
      padding: 20px;
      background: rgba(15, 23, 42, 0.5);
      border-radius: 16px;
      margin-top: 20px;
      opacity: 0;

      &.visible {
        animation: mapReveal 1.2s cubic-bezier(0.4, 0, 0.2, 1) forwards;
      }

      &::before {
        content: '';
        position: absolute;
        inset: 0;
        border-radius: 16px;
        padding: 2px;
        background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
        -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        -webkit-mask-composite: xor;
        mask-composite: exclude;
        animation: glowPulse 2s infinite;
      }
    }
  }

  &.story-section {
    p {
      position: relative;
      padding-left: 20px;

      &::before {
        content: '';
        position: absolute;
        left: 0;
        top: 0;
        width: 3px;
        height: 100%;
        background: linear-gradient(180deg, #5EEAD4, transparent);
        transform: scaleY(0);
        transform-origin: top;
        transition: transform 0.6s ease;
      }

      &.visible::before {
        transform: scaleY(1);
      }
    }
  }
`;

const PrintButton = styled.button`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  padding: 1rem 2rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  color: #0F172A;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 600;
  font-size: 1.1rem;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 0 30px rgba(94, 234, 212, 0.4);
  }

  @media (max-width: 768px) {
    position: static;
    width: 100%;
    margin-top: 1rem;
    border-radius: 12px;
  }
`;

// Ê∑ªÂä†ÂÖ®Â±ÄÂä®Áîª
const GlobalStyle = createGlobalStyle`
  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes glowPulse {
    0%, 100% { box-shadow: 0 0 20px rgba(94, 234, 212, 0.2); }
    50% { box-shadow: 0 0 40px rgba(94, 234, 212, 0.4); }
  }

  @keyframes mapReveal {
    0% { transform: scale(0.95); opacity: 0; }
    50% { transform: scale(1.02); }
    100% { transform: scale(1); opacity: 1; }
  }

  @keyframes slideInRight {
    from { transform: translateX(50px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
  }
`;

// Ê∑ªÂä†Êñ∞ÁöÑÁªÑ‰ª∂Áî®‰∫éÂú∞ÂõæÊ†áËÆ∞Âä®Áîª
const LocationMarker = styled.div`
  position: absolute;
  width: 20px;
  height: 20px;
  background: #5EEAD4;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  pointer-events: none;
  transition: all 0.3s ease;
  opacity: ${props => props.isScratched ? 1 : 0};

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: inherit;
    border-radius: inherit;
    animation: ripple 2s infinite;
  }

  @keyframes ripple {
    0% { transform: scale(1); opacity: 1; }
    100% { transform: scale(3); opacity: 0; }
  }
`;

// Ê∑ªÂä†Êñ∞ÁöÑÊ†∑ÂºèÁªÑ‰ª∂
const ScratchCardContainer = styled.div`
  position: relative;
  width: 100%;
  margin: 20px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;
`;

const ScratchArea = styled.div`
  position: relative;
  width: 100%;
  border-radius: 16px;
  overflow: hidden;
  min-height: 150px;
`;

const ScratchHint = styled.div`
  color: #5EEAD4;
  font-size: 1.5rem;
  font-weight: bold;
  text-shadow: 
    0 0 10px rgba(94, 234, 212, 0.5),
    0 0 20px rgba(94, 234, 212, 0.3),
    0 0 30px rgba(94, 234, 212, 0.2);
  opacity: ${props => props.isScratched ? 0 : 1};
  transition: opacity 0.3s ease;
  animation: pulse 2s infinite;
  text-align: center;

  @keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
  }
`;

// 1. È¶ñÂÖàÂÆö‰πâÂü∫Á°ÄÊ†∑ÂºèÁªÑ‰ª∂
const HiderAvatar = styled.div`
  width: 64px;
  height: 64px;
  border-radius: 50%;
  overflow: hidden;
  position: relative;
  flex-shrink: 0;
  border: 2px solid rgba(94, 234, 212, 0.3);

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const HiderName = styled.h3`
  color: #5EEAD4;
  font-size: 1.2rem;
  margin: 0;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  .gender-icon {
    color: #94A3B8;
    font-size: 0.9rem;
  }
`;

const HiderSignature = styled.p`
  color: #94A3B8;
  font-size: 0.9rem;
  margin: 0.5rem 0;
  font-style: italic;
`;

const StatItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.3rem;
  color: #94A3B8;
  font-size: 0.9rem;

  .icon {
    color: #5EEAD4;
  }
`;

const HiderStats = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 0.5rem;

  @media (max-width: 768px) {
    flex-wrap: wrap;
    justify-content: center;
    gap: 0.5rem;
  }
`;

const HiderInfo = styled.div`
  flex: 1;
`;

const LocationIcon = styled.div`
  font-size: 2rem;
  color: #5EEAD4;
`;

const LocationInfo = styled.div`
  flex: 1;

  h3 {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin: 0;

    .location-tag {
      font-size: 0.8rem;
      background: rgba(94, 234, 212, 0.1);
      padding: 0.2rem 0.5rem;
      border-radius: 12px;
      color: #5EEAD4;
    }
  }
`;

// 2. ÁÑ∂ÂêéÂÆö‰πâÂç°ÁâáÁªÑ‰ª∂
const InfoCard = styled.div`
  background: rgba(15, 23, 42, 0.5);
  border-radius: 16px;
  padding: 1.5rem;
  position: relative;
  overflow: hidden;

  @media (max-width: 768px) {
    padding: 1rem;
    border-radius: 12px;
  }
`;

const HiderCard = styled(InfoCard)`
  display: flex;
  gap: 1rem;
  align-items: center;

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;

    ${HiderAvatar} {
      margin: 0 auto 0.5rem;
    }
  }
`;

const LocationCard = styled(InfoCard)`
  display: flex;
  align-items: center;
  gap: 1rem;

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
  }
`;

// 3. ÊúÄÂêéÂÆö‰πâÂÆπÂô®ÁªÑ‰ª∂
const InfoContainer = styled.div`
  display: grid;
  gap: 1.5rem;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 1rem;
  }
`;

const InfoLabel = styled.div`
  color: #5EEAD4;
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const InfoValue = styled.div`
  color: #94A3B8;
  font-size: 0.9rem;

  span {
    display: inline-block;
    background: rgba(94, 234, 212, 0.1);
    padding: 0.2rem 0.5rem;
    border-radius: 12px;
    margin: 0.2rem;
  }
`;

const ScratchContent = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 30px;
  background: rgba(15, 23, 42, 0.8);
  border-radius: 16px;
  color: #5EEAD4;
  font-size: 1.3rem;
  text-align: center;
  letter-spacing: 1px;
  line-height: 1.6;
  font-weight: 500;

  p {
    margin: 0;
    text-shadow: 0 0 15px rgba(94, 234, 212, 0.5);
  }
`;

const ScratchCanvas = styled.canvas`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  cursor: pointer;
  touch-action: none;
`;

// Ê∑ªÂä†Êñ∞ÁöÑÊ†∑ÂºèÁªÑ‰ª∂
const SecretSection = styled.div`
  margin-top: 2rem;
  background: rgba(15, 23, 42, 0.8);
