function Explore() {
  const [activeFilter, setActiveFilter] = useState('全部');
  const [items, setItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showResults, setShowResults] = useState(false);

  // 模拟数据
  useEffect(() => {
    const mockItems = [
      {
        id: 1,
        title: '复古留声机',
        description: '一台来自80年代的索尼留声机，音质依然完美...',
        image: 'https://images.unsplash.com/photo-1461360228754-6e81c478b882',
        location: '东城区',
        price: '¥2999'
      },
      {
        id: 2,
        title: '手工木雕摆件',
        description: '老匠人精心雕刻的木质摆件，充满东方韵味...',
        image: 'https://images.unsplash.com/photo-1513519245088-0e12902e5a38',
        location: '西城区',
        price: '¥599'
      },
      // 可以添加更多模拟数据...
    ];

    setItems(mockItems);
  }, []);

  const filters = ['全部', '古董', '手工艺', '收藏品', '稀有物件'];

  // 搜索功能
  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);

    if (term.length > 0) {
      const filtered = items.filter(item => 
        item.title.toLowerCase().includes(term.toLowerCase()) ||
        item.description.toLowerCase().includes(term.toLowerCase())
      );
      setSearchResults(filtered);
      setShowResults(true);
    } else {
      setSearchResults([]);
      setShowResults(false);
    }
  };

  // 点击搜索结果项
  const handleResultClick = (item) => {
    setSearchTerm(item.title);
    setShowResults(false);
    // 这里可以添加其他处理逻辑,比如滚动到对应项
  };

  // 点击外部关闭搜索结果
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.search-container')) {
        setShowResults(false);
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <ExploreContainer>
      <TitleContainer>
        <Planet 
          size={60} 
          color="#4ECDC4" 
          style={{ top: '20%', left: '15%' }} 
        />
        <Planet 
          size={40} 
          color="#FFE66D" 
          style={{ top: '30%', right: '20%' }}
          duration="6s"
        />
        <Planet 
          size={30} 
          color="#FF6B6B" 
          style={{ bottom: '20%', left: '25%' }}
          duration="10s"
        />
        <Orbit />
        <Title>探索奇物</Title>
      </TitleContainer>

      <SearchContainer>
        <SearchWrapper className="search-container">
          <input 
            type="text"
            placeholder="搜索你感兴趣的物品..."
            value={searchTerm}
            onChange={handleSearch}
          />
          <SearchResults show={showResults}>
            {searchResults.map(item => (
              <SearchResultItem 
                key={item.id}
                onClick={() => handleResultClick(item)}
              >
                <img src={item.image} alt={item.title} />
                <div className="info">
                  <h4>{item.title}</h4>
                  <p>{item.description}</p>
                </div>
              </SearchResultItem>
            ))}
            {searchResults.length === 0 && searchTerm && (
              <SearchResultItem>
                <div className="info">
                  <h4>未找到相关结果</h4>
                  <p>试试其他关键词吧~ 🚀</p>
                </div>
              </SearchResultItem>
            )}
          </SearchResults>
        </SearchWrapper>

        <FilterContainer>
          {filters.map(filter => (
            <FilterTag 
              key={filter}
              active={activeFilter === filter}
              onClick={() => setActiveFilter(filter)}
            >
              {filter}
            </FilterTag>
          ))}
        </FilterContainer>
      </SearchContainer>

      <ItemGrid>
        {items.map((item, index) => (
          <ItemCard key={item.id} delay={index * 0.2}>
            <ItemImage src={item.image} alt={item.title} />
            <ItemInfo>
              <ItemTitle>{item.title}</ItemTitle>
              <ItemDescription>{item.description}</ItemDescription>
              <ItemFooter>
                <span>📍 {item.location}</span>
                <span>{item.price}</span>
              </ItemFooter>
            </ItemInfo>
          </ItemCard>
        ))}
      </ItemGrid>
    </ExploreContainer>
  );
}

