import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

// åŠ¨ç”»æ•ˆæœ
const float = keyframes`
  0% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(5deg); }
  100% { transform: translateY(0px) rotate(0deg); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #FF6B6B, 0 0 20px #FF6B6B; }
  100% { box-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #FF6B6B, 0 0 40px #FF6B6B; }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const rocketFly = keyframes`
  0% {
    transform: translateY(100vh) rotate(-45deg);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateY(-100vh) rotate(-45deg);
    opacity: 0;
  }
`;

const fadeInScale = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.8);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
`;

const HomeContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  padding: 2rem;
  background: #1a1a1a;
  color: #fff;
`;

const LightBeam = styled.div`
  position: absolute;
  width: 2px;
  height: 100%;
  background: linear-gradient(to bottom, transparent, #FF6B6B, transparent);
  opacity: 0.5;
  transform-origin: 50% 0;
  animation: ${float} 3s ease-in-out infinite;
  left: ${props => props.left}%;
  transform: rotate(${props => props.rotate}deg);
`;

const Planet = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: ${props => props.color};
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  animation: ${float} ${props => props.duration}s ease-in-out infinite;
  z-index: 1;
  box-shadow: 0 0 20px rgba(255,255,255,0.2);

  &::after {
    content: '';
    position: absolute;
    width: 110%;
    height: 110%;
    border-radius: 50%;
    border: 2px solid rgba(255,255,255,0.2);
    left: -5%;
    top: -5%;
    animation: ${pulse} 4s ease-in-out infinite;
  }
`;

const Title = styled.h1`
  font-size: 4.5rem;
  font-weight: bold;
  color: #fff;
  margin-bottom: 1rem;
  text-align: center;
  position: relative;
  z-index: 2;
  text-shadow: 0 0 10px rgba(255,107,107,0.5);
  animation: ${glow} 2s ease-in-out infinite alternate;
`;

const Subtitle = styled.p`
  font-size: 1.8rem;
  color: #FF6B6B;
  margin-bottom: 2rem;
  text-align: center;
  font-family: 'Noto Sans SC', sans-serif;
  position: relative;
  z-index: 2;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 2rem;
  margin-top: 2rem;
  position: relative;
  z-index: 2;
`;

const StyledButton = styled(Link)`
  padding: 1.2rem 2.5rem;
  font-size: 1.2rem;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      120deg,
      transparent,
      rgba(255, 255, 255, 0.2),
      transparent
    );
    transition: 0.5s;
  }

  &:hover::before {
    left: 100%;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 20px rgba(255,107,107,0.4);
  }

  &.primary {
    background: #FF6B6B;
    color: white;
  }

  &.secondary {
    background: transparent;
    border: 2px solid #4ECDC4;
    color: #4ECDC4;
  }
`;

const FunText = styled.span`
  position: absolute;
  font-size: 1rem;
  color: #FFE66D;
  transform: rotate(${props => props.rotate}deg);
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  z-index: 2;
`;

const Rocket = styled.div`
  position: fixed;
  width: 60px;
  height: 60px;
  right: 10%;
  z-index: 10;
  opacity: 0;
  pointer-events: none;
  
  &.flying {
    animation: ${rocketFly} 2s ease-in-out forwards;
  }

  &::before {
    content: 'ğŸš€';
    font-size: 40px;
    position: absolute;
    transform: rotate(45deg);
  }

  &::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 100px;
    background: linear-gradient(to bottom, #FF6B6B, transparent);
    transform: translateY(20px) rotate(45deg);
    filter: blur(10px);
    opacity: 0.5;
  }
`;

const ImageFrame = styled.div`
  position: relative;
  width: 300px;
  height: 200px;
  margin: 3rem auto;
  opacity: 0;
  transform: scale(0.8);

  &.visible {
    animation: ${fadeInScale} 1s ease-out forwards;
  }

  &::before {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    right: -20px;
    bottom: -20px;
    border: 2px solid #FF6B6B;
    border-radius: 10px;
    background: rgba(40, 40, 40, 0.8);
    backdrop-filter: blur(10px);
    z-index: -1;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 5px;
  }
`;

const ScrollSection = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  padding: 2rem;
`;

// æ·»åŠ æ–°çš„åŠ¨ç”»å…³é”®å¸§
const flyUpAnimation = keyframes`
  0% {
    transform: translate(var(--startX), 100vh) rotate(var(--rotation));
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translate(var(--endX), -100vh) rotate(var(--finalRotation));
    opacity: 0;
  }
`;

const FloatingElement = styled.div`
  position: fixed;
  font-size: ${props => props.size || '2rem'};
  z-index: 5;
  pointer-events: none;
  animation: ${flyUpAnimation} var(--duration) ease-out forwards;
  left: 0;
  top: 0;
  transform: translateY(100vh);
`;

const FLOATING_ELEMENTS = [
  'ğŸš€', 'âœ¨', 'ğŸ¸', 'ğŸ¹', 'ğŸº', 'ğŸ­', 'ğŸ¨', 'ğŸ®', 
  'ğŸŒŸ', 'ğŸŒ', 'ğŸŒ˜', 'ğŸ¯', 'ğŸª', 'ğŸµ', 'ğŸ¼', 'ğŸ§'
];

function getRandomValue(min, max) {
  return Math.random() * (max - min) + min;
}

function FloatingElements() {
  const [elements, setElements] = useState([]);

  useEffect(() => {
    const createNewElement = () => {
      const id = Date.now();
      const element = FLOATING_ELEMENTS[Math.floor(Math.random() * FLOATING_ELEMENTS.length)];
      const startX = getRandomValue(-20, window.innerWidth - 50);
      const endX = startX + getRandomValue(-200, 200);
      const duration = getRandomValue(3, 8);
      const size = getRandomValue(1.5, 3) + 'rem';
      const rotation = getRandomValue(-45, 45);
      const finalRotation = rotation + getRandomValue(-180, 180);

      setElements(prev => [...prev, {
        id,
        element,
        style: {
          '--startX': `${startX}px`,
          '--endX': `${endX}px`,
          '--duration': `${duration}s`,
          '--rotation': `${rotation}deg`,
          '--finalRotation': `${finalRotation}deg`
        },
        size
      }]);

      // åŠ¨ç”»ç»“æŸåç§»é™¤å…ƒç´ 
      setTimeout(() => {
        setElements(prev => prev.filter(e => e.id !== id));
      }, duration * 1000);
    };

    // åˆå§‹åˆ›å»ºå‡ ä¸ªå…ƒç´ 
    for (let i = 0; i < 5; i++) {
      setTimeout(createNewElement, i * 500);
    }

    // å®šæœŸåˆ›å»ºæ–°å…ƒç´ 
    const interval = setInterval(() => {
      if (Math.random() < 0.7) { // 70%çš„æ¦‚ç‡åˆ›å»ºæ–°å…ƒç´ 
        createNewElement();
      }
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return elements.map(({ id, element, style, size }) => (
    <FloatingElement key={id} style={style} size={size}>
      {element}
    </FloatingElement>
  ));
}

// ä¿®æ”¹å…‰æŸ±åŠ¨ç”»ä¸ºæµæ˜ŸåŠ¨ç”»
const meteorFall = keyframes`
  0% {
    transform: translate(120%, -120%) rotate(45deg);
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translate(-120%, 120%) rotate(45deg);
    opacity: 0;
  }
`;

// æ›¿æ¢åŸæ¥çš„ ColorBeam ç»„ä»¶
const Meteor = styled.div`
  position: absolute;
  width: 150px;
  height: 2px;
  background: linear-gradient(
    90deg,
    transparent,
    ${props => props.color || '#FF6B6B'}
  );
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  filter: blur(3px);
  opacity: 0;
  z-index: 1;
  transform-origin: left center;
  animation: ${meteorFall} ${props => props.duration || 3}s linear infinite;
  animation-delay: ${props => props.delay || '0s'};

  &::after {
    content: '';
    position: absolute;
    width: 15px;
    height: 15px;
    border-radius: 50%;
    background: ${props => props.color || '#FF6B6B'};
    right: 0;
    top: 50%;
    transform: translateY(-50%);
    filter: blur(4px);
  }
`;

// æ·»åŠ æ–°çš„æ ·å¼ç»„ä»¶
const CitySection = styled(ScrollSection)`
  background: #1a1a1a;
  position: relative;
  overflow: hidden;
  padding: 0;
  margin: 0;
`;

const CityScape = styled.div`
  position: absolute;
  bottom: 0;
  width: 100%;
  height: 60%;
  background: linear-gradient(to bottom, transparent, #1a1a1a),
    url('/city-silhouette.png') repeat-x bottom;
  background-size: contain;
  z-index: 2;
`;

const NeonPlanet = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: ${props => props.color};
  box-shadow: 0 0 30px ${props => props.color}aa,
             inset -10px -10px 20px rgba(0,0,0,0.5);
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  z-index: ${props => props.zIndex || 1};
  animation: ${float} ${props => props.duration || 6}s ease-in-out infinite;
  animation-delay: ${props => props.delay || '0s'};

  &::after {
    content: '';
    position: absolute;
    width: 120%;
    height: 120%;
    border-radius: 50%;
    border: 2px solid ${props => props.color}44;
    left: -10%;
    top: -10%;
    animation: ${pulse} 4s ease-in-out infinite;
  }
`;

const ButtonBar = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 80px;
  background: linear-gradient(to top, #1a1a1a, transparent);
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  z-index: 10;
`;

const IconButton = styled.button`
  background: transparent;
  border: none;
  color: ${props => props.color || '#4ECDC4'};
  font-size: 1.5rem;
  cursor: pointer;
  position: relative;
  padding: 1rem;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: ${props => props.color || '#4ECDC4'}22;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    transition: all 0.3s ease;
  }

  &:hover::before {
    transform: translate(-50%, -50%) scale(1.2);
  }
`;

// æ·»åŠ æ–°çš„åŠ¨ç”»æ•ˆæœ
const slideInFromRight = keyframes`
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

const slideInFromLeft = keyframes`
  from {
    transform: translateX(-100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

const CardSection = styled.div`
  min-height: 100vh;
  padding: 4rem 2rem;
  position: relative;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
  z-index: 2;
`;

const StoryCard = styled.div`
  background: rgba(30, 30, 30, 0.8);
  border-radius: 20px;
  padding: 2rem;
  backdrop-filter: blur(10px);
