import React, { useEffect, useState, useRef, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';

const DetailContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0F172A 0%, #1E293B 100%);
  color: #fff;
  padding: 2rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }
`;

const PrintableContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: rgba(30, 41, 59, 0.8);
  border-radius: 24px;
  padding: 2.5rem;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(94, 234, 212, 0.2);
  box-shadow: 0 0 40px rgba(94, 234, 212, 0.1);
  position: relative;

  &::before {
    content: '';
    position: absolute;
    inset: -2px;
    border-radius: 25px;
    padding: 2px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }
`;

const ItemHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;

  h1 {
    color: #5EEAD4;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(94, 234, 212, 0.3);
    font-weight: 700;
  }

  .rarity-badge {
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    padding: 0.5rem 1.5rem;
    border-radius: 20px;
    font-weight: 600;
    display: inline-block;
    transform: translateY(-5px);
    animation: float 3s ease-in-out infinite;
  }
`;

const ItemImage = styled.div`
  position: relative;
  margin-bottom: 3rem;

  img {
    width: 100%;
    height: 400px;
    object-fit: cover;
    border-radius: 16px;
    border: 2px solid rgba(94, 234, 212, 0.3);
  }

  &::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    background: linear-gradient(180deg, transparent 0%, rgba(15, 23, 42, 0.2) 100%);
  }
`;

const ItemSection = styled.section`
  margin-bottom: 2.5rem;
  opacity: 0;
  transform: translateY(20px);

  &.visible {
    animation: fadeIn 0.8s ease-out forwards;
  }

  &.map-section {
    .map-container {
      position: relative;
      padding: 20px;
      background: rgba(15, 23, 42, 0.5);
      border-radius: 16px;
      margin-top: 20px;
      opacity: 0;

      &.visible {
        animation: mapReveal 1.2s cubic-bezier(0.4, 0, 0.2, 1) forwards;
      }

      &::before {
        content: '';
        position: absolute;
        inset: 0;
        border-radius: 16px;
        padding: 2px;
        background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
        -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        -webkit-mask-composite: xor;
        mask-composite: exclude;
        animation: glowPulse 2s infinite;
      }
    }
  }

  &.story-section {
    p {
      position: relative;
      padding-left: 20px;

      &::before {
        content: '';
        position: absolute;
        left: 0;
        top: 0;
        width: 3px;
        height: 100%;
        background: linear-gradient(180deg, #5EEAD4, transparent);
        transform: scaleY(0);
        transform-origin: top;
        transition: transform 0.6s ease;
      }

      &.visible::before {
        transform: scaleY(1);
      }
    }
  }
`;

const PrintButton = styled.button`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  padding: 1rem 2rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  color: #0F172A;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 600;
  font-size: 1.1rem;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 0 30px rgba(94, 234, 212, 0.4);
  }
`;

// 添加全局动画
const GlobalStyle = createGlobalStyle`
  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes glowPulse {
    0%, 100% { box-shadow: 0 0 20px rgba(94, 234, 212, 0.2); }
    50% { box-shadow: 0 0 40px rgba(94, 234, 212, 0.4); }
  }

  @keyframes mapReveal {
    0% { transform: scale(0.95); opacity: 0; }
    50% { transform: scale(1.02); }
    100% { transform: scale(1); opacity: 1; }
  }

  @keyframes slideInRight {
    from { transform: translateX(50px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
  }
`;

// 添加新的组件用于地图标记动画
const LocationMarker = styled.div`
  position: absolute;
  width: 20px;
  height: 20px;
  background: #5EEAD4;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  pointer-events: none;
  transition: all 0.3s ease;
  opacity: ${props => props.isScratched ? 1 : 0};

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: inherit;
    border-radius: inherit;
    animation: ripple 2s infinite;
  }

  @keyframes ripple {
    0% { transform: scale(1); opacity: 1; }
    100% { transform: scale(3); opacity: 0; }
  }
`;

// 添加新的样式组件
const ScratchCardContainer = styled.div`
  position: relative;
  width: 100%;
  margin: 20px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;
`;

const ScratchArea = styled.div`
  position: relative;
  width: 100%;
  border-radius: 16px;
  overflow: hidden;
  min-height: 150px;
`;

const ScratchHint = styled.div`
  color: #5EEAD4;
  font-size: 1.5rem;
  font-weight: bold;
  text-shadow: 
    0 0 10px rgba(94, 234, 212, 0.5),
    0 0 20px rgba(94, 234, 212, 0.3),
    0 0 30px rgba(94, 234, 212, 0.2);
  opacity: ${props => props.isScratched ? 0 : 1};
  transition: opacity 0.3s ease;
  animation: pulse 2s infinite;
  text-align: center;

  @keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
  }
`;

// 添加新的样式组件
const InfoContainer = styled.div`
  display: grid;
  gap: 1.5rem;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
`;

const InfoCard = styled.div`
  background: rgba(15, 23, 42, 0.5);
  border-radius: 16px;
  padding: 1.5rem;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    padding: 1px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(94, 234, 212, 0.2);
  }
`;

const LocationCard = styled(InfoCard)`
  grid-column: 1 / -1;
  display: flex;
  align-items: center;
  gap: 1rem;
  background: linear-gradient(135deg, rgba(15, 23, 42, 0.8), rgba(30, 41, 59, 0.8));

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }
`;

const LocationIcon = styled.div`
  width: 48px;
  height: 48px;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  flex-shrink: 0;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: inherit;
    filter: blur(8px);
    opacity: 0.4;
    animation: pulse 2s infinite;
  }

  @keyframes pulse {
    0% { transform: scale(1); opacity: 0.4; }
    50% { transform: scale(1.2); opacity: 0.2; }
    100% { transform: scale(1); opacity: 0.4; }
  }
`;

const LocationInfo = styled.div`
  flex: 1;

  h3 {
    color: #5EEAD4;
    font-size: 1.2rem;
    margin: 0 0 0.5rem 0;
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }

  p {
    color: #94A3B8;
    margin: 0;
    font-size: 1rem;
    line-height: 1.6;
  }

  .location-tag {
    display: inline-block;
    padding: 0.25rem 0.75rem;
    background: rgba(94, 234, 212, 0.1);
    border-radius: 20px;
    color: #5EEAD4;
    font-size: 0.875rem;
    margin-top: 0.5rem;
    border: 1px solid rgba(94, 234, 212, 0.2);
  }
`;

const InfoLabel = styled.div`
  font-size: 0.875rem;
  color: #94A3B8;
  margin-bottom: 0.5rem;
`;

const InfoValue = styled.div`
  color: #5EEAD4;
  font-size: 1.1rem;
  font-weight: 500;
`;

const ScratchContent = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 30px;
  background: rgba(15, 23, 42, 0.8);
  border-radius: 16px;
  color: #5EEAD4;
  font-size: 1.3rem;
  text-align: center;
  letter-spacing: 1px;
  line-height: 1.6;
  font-weight: 500;

  p {
    margin: 0;
    text-shadow: 0 0 15px rgba(94, 234, 212, 0.5);
  }
`;

const ScratchCanvas = styled.canvas`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  cursor: pointer;
  touch-action: none;
`;

function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const canvasRef = useRef(null);
  const [isScratched, setIsScratched] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });

  useEffect(() => {
    // 这里应该是从API获取数据
    // 暂时使用模拟数据
    const mockItem = {
      id: parseInt(id),
      title: '神秘的青铜门把手',
      description: '这是一个雕刻着龙纹的古老门把，据说曾经属于一位著名的收藏家...',
      image: 'https://images.unsplash.com/photo-1461360228754-6e81c478b882',
      location: '知识花园区域',
      rarity: 'SSR',
      clue: '在书架与书架之间，寻找一扇被遗忘的门',
      story: '传说这个门把手曾经是通往一个秘密藏书室的钥匙，只有在月圆之夜才会显现其真正的模样。',
      discoveredBy: '探索者小明',
      discoveryDate: '2024-01-15',
      additionalInfo: '这个门把手上的龙纹在特定角度下会泛出青色的光芒...',
      discoveryStory: '那是一个月圆之夜，我在理古籍时意外发现了这个门把手...'
    };

    setItem(mockItem);
  }, [id]);

  const handlePrint = () => {
    window.print();
  };

  // 修改：将观察者逻辑移动到这个 useEffect 中，并添加 item 依赖
  useEffect(() => {
    if (!item) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.2 }
    );

    // 同时观察 ItemSection 和 map-container
    document.querySelectorAll('.item-section, .map-container').forEach(element => {
      observer.observe(element);
    });

    return () => observer.disconnect();
  }, [item]); // 添加 item 作为依赖

  // 初始化刮刮卡
  const initScratchCard = useCallback(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // 设置画布尺寸
    const updateCanvasSize = () => {
      const container = canvas.parentElement;
      canvas.width = container.offsetWidth;
      canvas.height = container.offsetHeight;

      // 填充初始遮罩
      ctx.fillStyle = '#1E293B';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // 添加一些花纹使其看起来更有趣
      ctx.strokeStyle = '#2DD4BF';
      for (let i = 0; i < canvas.width; i += 20) {
        for (let j = 0; j < canvas.height; j += 20) {
          ctx.beginPath();
          ctx.arc(i, j, 1, 0, Math.PI * 2);
          ctx.stroke();
        }
      }
    };

    updateCanvasSize();
    window.addEventListener('resize', updateCanvasSize);
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  // 处理刮擦效果
  const handleScratch = useCallback((e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const rect = canvas.getBoundingClientRect();

    // 获取鼠标/触摸位置
    const x = (e.clientX || e.touches[0].clientX) - rect.left;
    const y = (e.clientY || e.touches[0].clientY) - rect.top;

    ctx.globalCompositeOperation = 'destination-out';
    ctx.beginPath();
    ctx.arc(x, y, 20, 0, Math.PI * 2);
    ctx.fill();

    // 检查已刮开的面积
    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    const pixels = imageData.data;
    let transparentPixels = 0;

    for (let i = 0; i < pixels.length; i += 4) {
      if (pixels[i + 3] === 0) transparentPixels++;
    }

    const totalPixels = pixels.length / 4;
    if (transparentPixels / totalPixels > 0.5) {
      setIsScratched(true);
      canvas.style.display = 'none';
    }
  }, [isDrawing]);

  useEffect(() => {
    if (item) {
      const cleanup = initScratchCard();
      return cleanup;
    }
  }, [item, initScratchCard]);

  // 修改：更新鼠标移动处理函数
  const handleMouseMove = useCallback((e) => {
    if (!isScratched) return;

    const scratchArea = e.currentTarget;
    const rect = scratchArea.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;

    setMousePos({ x, y });
  }, [isScratched]);

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <DetailContainer>
      <GlobalStyle />
      <PrintableContent>
        <ItemHeader>
          <h1>{item.title}</h1>
          <p className="rarity-badge">{item.rarity}</p>
        </ItemHeader>

        <ItemImage>
          <img src={item.image} alt={item.title} />
        </ItemImage>

        <ItemSection className="item-section">
          <h2>📜 物品描述</h2>
          <p>{item.description}</p>
        </ItemSection>

        <ItemSection className="item-section map-section">
          <h2>🗺️ 寻宝线索</h2>
          <ScratchCardContainer>
            <ScratchHint isScratched={isScratched}>
              ✨ 刮开查看线索 ✨
            </ScratchHint>

            <ScratchArea onMouseMove={handleMouseMove}>
              <ScratchContent>
                <p>{item.clue}</p>
                <LocationMarker 
                  isScratched={isScratched}
                  style={{ 
                    left: `${mousePos.x}%`, 
                    top: `${mousePos.y}%`,
                    display: isScratched ? 'block' : 'none'
                  }} 
                />
              </ScratchContent>
              <ScratchCanvas
                ref={canvasRef}
                onMouseDown={() => setIsDrawing(true)}
                onMouseUp={() => setIsDrawing(false)}
                onMouseMove={handleScratch}
                onMouseLeave={() => setIsDrawing(false)}
                onTouchStart={() => setIsDrawing(true)}
                onTouchEnd={() => setIsDrawing(false)}
                onTouchMove={handleScratch}
              />
            </ScratchArea>
          </ScratchCardContainer>
        </ItemSection>

        <ItemSection className="item-section story-section">
          <h2>📖 背后的故事</h2>
          <p>{item.story}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>🔍 发现过程</h2>
          <p>{item.discoveryStory}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>ℹ️ 额外信息</h2>
          <p>{item.additionalInfo}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>📍 基本信息</h2>
          <InfoContainer>
            <LocationCard>
              <LocationIcon>📍</LocationIcon>
              <LocationInfo>
                <h3>
                  探索地点
                  <span className="location-tag">重要线索</span>
                </h3>
                <p>{item.location}</p>
                <p className="location-tag">知识花园区域</p>
              </LocationInfo>
            </LocationCard>

            <InfoCard>
              <InfoLabel>隐藏者</InfoLabel>
              <InfoValue>
                <span role="img" aria-label="explorer">🔍</span> {item.discoveredBy}
              </InfoValue>
            </InfoCard>

            <InfoCard>
              <InfoLabel>隐藏时间</InfoLabel>
              <InfoValue>
                <span role="img" aria-label="calendar">📅</span> {item.discoveryDate}
              </InfoValue>
            </InfoCard>
          </InfoContainer>
        </ItemSection>
      </PrintableContent>

      <PrintButton onClick={handlePrint}>
        🖨️ 打印页面
      </PrintButton>
    </DetailContainer>
  );
}

export default ItemDetail;
