import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    email: '',
    nickname: '',
    avatar: null,
    gender: '',
    signature: '',
    tags: [],
    status: 'Ê≠£Âú®ÂØªÊâæÂøóÂêåÈÅìÂêàÁöÑÊé¢Á¥¢ËÄÖ~',
  });
  
  const [currentStep, setCurrentStep] = useState(1);
  const [previewUrl, setPreviewUrl] = useState('');
  const [error, setError] = useState('');
  const [shootingStars, setShootingStars] = useState([]);
  const navigate = useNavigate();

  // È¢ÑËÆæÊ†áÁ≠æÈÄâÈ°π
  const tagOptions = [
    'Êé¢Á¥¢Ëææ‰∫∫', 'ÊïÖ‰∫ãÊî∂ÈõÜËÄÖ', 'ÂüéÂ∏ÇÊº´Ê∏∏', 'ÊñáÂåñÂØªÂÆù',
    'ÂéÜÂè≤Áà±Â•ΩËÄÖ', 'ÊëÑÂΩ±Áé©ÂÆ∂', 'Ëß£Ë∞úÈ´òÊâã', 'Á§æ‰∫§Ëææ‰∫∫'
  ];

  // Â§ÑÁêÜÂ§¥ÂÉè‰∏ä‰º†
  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        setError('ÂõæÁâáÂ§ßÂ∞è‰∏çËÉΩË∂ÖËøá5MB');
        return;
      }
      setFormData({ ...formData, avatar: file });
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  // Â§ÑÁêÜÊ†áÁ≠æÈÄâÊã©
  const handleTagToggle = (tag) => {
    const newTags = formData.tags.includes(tag)
      ? formData.tags.filter(t => t !== tag)
      : [...formData.tags, tag].slice(0, 4);
    setFormData({ ...formData, tags: newTags });
  };

  // Ê∑ªÂä†ÊµÅÊòüÊïàÊûú
  useEffect(() => {
    const interval = setInterval(() => {
      const newStar = {
        id: Date.now(),
        top: Math.random() * 100,
        left: Math.random() * 100,
        duration: 1 + Math.random() * 2
      };
      setShootingStars(prev => [...prev, newStar]);
      setTimeout(() => {
        setShootingStars(prev => prev.filter(star => star.id !== newStar.id));
      }, newStar.duration * 1000);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  // Ë°®ÂçïÊèê‰∫§Â§ÑÁêÜ
  const handleSubmit = async (e) => {
    e.preventDefault();
    // ËøôÈáåÊ∑ªÂä†Ë°®ÂçïÈ™åËØÅÂíåÊèê‰∫§ÈÄªËæë
    try {
      // Ê®°ÊãüAPIË∞ÉÁî®
      await new Promise(resolve => setTimeout(resolve, 1000));
      navigate('/explore');
    } catch (error) {
      setError('Ê≥®ÂÜåÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  return (
    <RegisterContainer>
      <CosmicBackground>
        {shootingStars.map(star => (
          <ShootingStar
            key={star.id}
            style={{
              top: `${star.top}%`,
              left: `${star.left}%`,
              animationDuration: `${star.duration}s`
            }}
          />
        ))}
      </CosmicBackground>

      <RegisterCard>
        <CardTitle>
          <span>‚ú®</span> ÂºÄÂêØ‰Ω†ÁöÑÊé¢Á¥¢‰πãÊóÖ <span>‚ú®</span>
        </CardTitle>

        <StepIndicator>
          {[1, 2, 3].map(step => (
            <StepDot key={step} active={step === currentStep} completed={step < currentStep} />
          ))}
        </StepIndicator>

        <Form onSubmit={handleSubmit}>
          {currentStep === 1 && (
            <StepContent>
              <FormGroup>
                <Label>Áî®Êà∑Âêç</Label>
                <Input
                  type="text"
                  value={formData.username}
                  onChange={e => setFormData({...formData, username: e.target.value})}
                  placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÔºàÁî®‰∫éÁôªÂΩïÔºâ"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>ÂØÜÁ†Å</Label>
                <Input
                  type="password"
                  value={formData.password}
                  onChange={e => setFormData({...formData, password: e.target.value})}
                  placeholder="ËØ∑ËÆæÁΩÆÂØÜÁ†Å"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>Á°ÆËÆ§ÂØÜÁ†Å</Label>
                <Input
                  type="password"
                  value={formData.confirmPassword}
                  onChange={e => setFormData({...formData, confirmPassword: e.target.value})}
                  placeholder="ËØ∑ÂÜçÊ¨°ËæìÂÖ•ÂØÜÁ†Å"
                  required
                />
              </FormGroup>
            </StepContent>
          )}

          {currentStep === 2 && (
            <StepContent>
              <AvatarUpload>
                <AvatarPreview>
                  {previewUrl ? (
                    <img src={previewUrl} alt="Â§¥ÂÉèÈ¢ÑËßà" />
                  ) : (
                    <span>üì∏</span>
                  )}
