import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    email: '',
    nickname: '',
    avatar: null,
    gender: '',
    signature: '',
    tags: [],
    status: '正在寻找志同道合的探索者~',
  });
  
  const [currentStep, setCurrentStep] = useState(1);
  const [previewUrl, setPreviewUrl] = useState('');
  const [error, setError] = useState('');
  const [shootingStars, setShootingStars] = useState([]);
  const navigate = useNavigate();

  // 预设标签选项
  const tagOptions = [
    '探索达人', '故事收集者', '城市漫游', '文化寻宝',
    '历史爱好者', '摄影玩家', '解谜高手', '社交达人'
  ];

  // 处理头像上传
  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        setError('图片大小不能超过5MB');
        return;
      }
      setFormData({ ...formData, avatar: file });
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  // 处理标签选择
  const handleTagToggle = (tag) => {
    const newTags = formData.tags.includes(tag)
      ? formData.tags.filter(t => t !== tag)
      : [...formData.tags, tag].slice(0, 4);
    setFormData({ ...formData, tags: newTags });
  };

  // 添加流星效果
  useEffect(() => {
    const interval = setInterval(() => {
      const newStar = {
        id: Date.now(),
        top: Math.random() * 100,
        left: Math.random() * 100,
        duration: 1 + Math.random() * 2
      };
      setShootingStars(prev => [...prev, newStar]);
      setTimeout(() => {
        setShootingStars(prev => prev.filter(star => star.id !== newStar.id));
      }, newStar.duration * 1000);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  // 表单提交处理
  const handleSubmit = async (e) => {
    e.preventDefault();
    // 这里添加表单验证和提交逻辑
    try {
      // 模拟API调用
      await new Promise(resolve => setTimeout(resolve, 1000));
      navigate('/explore');
    } catch (error) {
      setError('注册失败，请重试');
    }
  };

  return (
    <RegisterContainer>
      <CosmicBackground>
        {shootingStars.map(star => (
          <ShootingStar
            key={star.id}
            style={{
              top: `${star.top}%`,
              left: `${star.left}%`,
              animationDuration: `${star.duration}s`
            }}
          />
        ))}
      </CosmicBackground>

      <RegisterCard>
        <CardTitle>
          <span>✨</span> 开启你的探索之旅 <span>✨</span>
        </CardTitle>

        <StepIndicator>
          {[1, 2, 3].map(step => (
            <StepDot key={step} active={step === currentStep} completed={step < currentStep} />
          ))}
        </StepIndicator>

        <Form onSubmit={handleSubmit}>
          {currentStep === 1 && (
            <StepContent>
              <FormGroup>
                <Label>用户名</Label>
                <Input
                  type="text"
                  value={formData.username}
                  onChange={e => setFormData({...formData, username: e.target.value})}
                  placeholder="请输入用户名（用于登录）"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>密码</Label>
                <Input
                  type="password"
                  value={formData.password}
                  onChange={e => setFormData({...formData, password: e.target.value})}
                  placeholder="请设置密码"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>确认密码</Label>
                <Input
                  type="password"
                  value={formData.confirmPassword}
                  onChange={e => setFormData({...formData, confirmPassword: e.target.value})}
                  placeholder="请再次输入密码"
                  required
                />
              </FormGroup>
            </StepContent>
          )}

          {currentStep === 2 && (
            <StepContent>
              <AvatarUpload>
                <AvatarPreview>
                  {previewUrl ? (
                    <img src={previewUrl} alt="头像预览" />
                  ) : (
                    <span>📸</span>
                  )}
                </AvatarPreview>
                <AvatarInput
                  type="file"
                  accept="image/*"
                  onChange={handleAvatarChange}
                  id="avatar-upload"
                />
                <UploadLabel htmlFor="avatar-upload">
                  选择头像
                </UploadLabel>
              </AvatarUpload>
              <FormGroup>
                <Label>昵称</Label>
                <Input
                  type="text"
                  value={formData.nickname}
                  onChange={e => setFormData({...formData, nickname: e.target.value})}
                  placeholder="给自己起个好听的昵称吧"
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label>性别</Label>
                <GenderSelect>
                  <GenderOption
                    selected={formData.gender === 'male'}
                    onClick={() => setFormData({...formData, gender: 'male'})}
                  >
                    👨 男生
                  </GenderOption>
                  <GenderOption
                    selected={formData.gender === 'female'}
                    onClick={() => setFormData({...formData, gender: 'female'})}
                  >
                    👩 女生
                  </GenderOption>
                </GenderSelect>
              </FormGroup>
            </StepContent>
          )}

          {currentStep === 3 && (
            <StepContent>
              <FormGroup>
                <Label>个性签名</Label>
                <TextArea
                  value={formData.signature}
                  onChange={e => setFormData({...formData, signature: e.target.value})}
                  placeholder="写下你的探索宣言..."
                  maxLength={50}
                />
              </FormGroup>
              <FormGroup>
                <Label>选择标签（最多4个）</Label>
                <TagsContainer>
                  {tagOptions.map(tag => (
                    <TagButton
                      key={tag}
                      selected={formData.tags.includes(tag)}
                      onClick={() => handleTagToggle(tag)}
                      type="button"
                    >
                      {tag}
                    </TagButton>
                  ))}
                </TagsContainer>
              </FormGroup>
            </StepContent>
          )}

          {error && <ErrorMessage>{error}</ErrorMessage>}

          <ButtonGroup>
            {currentStep > 1 && (
              <StepButton type="button" onClick={() => setCurrentStep(prev => prev - 1)}>
                上一步
              </StepButton>
            )}
            {currentStep < 3 ? (
              <StepButton type="button" onClick={() => setCurrentStep(prev => prev + 1)}>
                下一步
              </StepButton>
            ) : (
              <SubmitButton type="submit">开启探索之旅</SubmitButton>
            )}
          </ButtonGroup>
        </Form>
      </RegisterCard>
    </RegisterContainer>
  );
}

