import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';

const DetailContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0F172A 0%, #1E293B 100%);
  color: #fff;
  padding: 2rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: url('/assets/grid.svg') repeat;
    opacity: 0.1;
    pointer-events: none;
  }
`;

const PrintableContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: rgba(30, 41, 59, 0.8);
  border-radius: 24px;
  padding: 2.5rem;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(94, 234, 212, 0.2);
  box-shadow: 0 0 40px rgba(94, 234, 212, 0.1);
  position: relative;

  &::before {
    content: '';
    position: absolute;
    inset: -2px;
    border-radius: 25px;
    padding: 2px;
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    -webkit-mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    mask: 
      linear-gradient(#fff 0 0) content-box, 
      linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }
`;

const ItemHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;

  h1 {
    color: #5EEAD4;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(94, 234, 212, 0.3);
    font-weight: 700;
  }

  .rarity-badge {
    background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
    padding: 0.5rem 1.5rem;
    border-radius: 20px;
    font-weight: 600;
    display: inline-block;
    transform: translateY(-5px);
    animation: float 3s ease-in-out infinite;
  }
`;

const ItemImage = styled.div`
  position: relative;
  margin-bottom: 3rem;

  img {
    width: 100%;
    height: 400px;
    object-fit: cover;
    border-radius: 16px;
    border: 2px solid rgba(94, 234, 212, 0.3);
  }

  &::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 16px;
    background: linear-gradient(180deg, transparent 0%, rgba(15, 23, 42, 0.2) 100%);
  }
`;

const ItemSection = styled.section`
  margin-bottom: 2.5rem;
  opacity: 0;
  transform: translateY(20px);

  &.visible {
    animation: fadeIn 0.8s ease-out forwards;
  }

  &.map-section {
    .map-container {
      position: relative;
      padding: 20px;
      background: rgba(15, 23, 42, 0.5);
      border-radius: 16px;
      margin-top: 20px;
      opacity: 0;

      &.visible {
        animation: mapReveal 1.2s cubic-bezier(0.4, 0, 0.2, 1) forwards;
      }

      &::before {
        content: '';
        position: absolute;
        inset: 0;
        border-radius: 16px;
        padding: 2px;
        background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
        -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
        -webkit-mask-composite: xor;
        mask-composite: exclude;
        animation: glowPulse 2s infinite;
      }
    }
  }

  &.story-section {
    p {
      position: relative;
      padding-left: 20px;

      &::before {
        content: '';
        position: absolute;
        left: 0;
        top: 0;
        width: 3px;
        height: 100%;
        background: linear-gradient(180deg, #5EEAD4, transparent);
        transform: scaleY(0);
        transform-origin: top;
        transition: transform 0.6s ease;
      }

      &.visible::before {
        transform: scaleY(1);
      }
    }
  }
`;

const PrintButton = styled.button`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  padding: 1rem 2rem;
  background: linear-gradient(45deg, #5EEAD4, #2DD4BF);
  color: #0F172A;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 600;
  font-size: 1.1rem;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 0 30px rgba(94, 234, 212, 0.4);
  }
`;

// 添加全局动画
const GlobalStyle = createGlobalStyle`
  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes glowPulse {
    0%, 100% { box-shadow: 0 0 20px rgba(94, 234, 212, 0.2); }
    50% { box-shadow: 0 0 40px rgba(94, 234, 212, 0.4); }
  }

  @keyframes mapReveal {
    0% { transform: scale(0.95); opacity: 0; }
    50% { transform: scale(1.02); }
    100% { transform: scale(1); opacity: 1; }
  }

  @keyframes slideInRight {
    from { transform: translateX(50px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
  }
`;

// 添加新的组件用于地图标记动画
const LocationMarker = styled.div`
  position: absolute;
  width: 20px;
  height: 20px;
  background: #5EEAD4;
  border-radius: 50%;
  transform: translate(-50%, -50%);

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: inherit;
    border-radius: inherit;
    animation: ripple 2s infinite;
  }

  @keyframes ripple {
    0% { transform: scale(1); opacity: 1; }
    100% { transform: scale(3); opacity: 0; }
  }
`;

function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);

  useEffect(() => {
    // 这里应该是从API获取数据
    // 暂时使用模拟数据
    const mockItem = {
      id: parseInt(id),
      title: '神秘的青铜门把手',
      description: '这是一个雕刻着龙纹的古老门把手，据说曾经属于一位著名的收藏家...',
      image: 'https://images.unsplash.com/photo-1461360228754-6e81c478b882',
      location: '知识花园区域',
      rarity: 'SSR',
      clue: '在书架与书架之间，寻找一扇被遗忘的门',
      story: '传说这个门把手曾经是通往一个秘密藏书室的钥匙，只有在月圆之夜才会显现其真正的模样。',
      discoveredBy: '探索者小明',
      discoveryDate: '2024-01-15',
      additionalInfo: '这个门把手上的龙纹在特定角度下会泛出青色的光芒...',
      discoveryStory: '那是一个月圆之夜，我在理古籍时意外发现了这个门把手...'
    };

    setItem(mockItem);
  }, [id]);

  const handlePrint = () => {
    window.print();
  };

  // 修改：将观察者逻辑移动到这个 useEffect 中，并添加 item 依赖
  useEffect(() => {
    if (!item) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.2 }
    );

    // 同时观察 ItemSection 和 map-container
    document.querySelectorAll('.item-section, .map-container').forEach(element => {
      observer.observe(element);
    });

    return () => observer.disconnect();
  }, [item]); // 添加 item 作为依赖

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <DetailContainer>
      <GlobalStyle />
      <PrintableContent>
        <ItemHeader>
          <h1>{item.title}</h1>
          <p className="rarity-badge">{item.rarity}</p>
        </ItemHeader>

        <ItemImage>
          <img src={item.image} alt={item.title} />
        </ItemImage>

        <ItemSection className="item-section">
          <h2>📜 物品描述</h2>
          <p>{item.description}</p>
        </ItemSection>

        <ItemSection className="item-section map-section">
          <h2>🗺️ 寻宝线索</h2>
          <div className="map-container">
            <p>{item.clue}</p>
            <LocationMarker style={{ left: '50%', top: '50%' }} />
          </div>
        </ItemSection>

        <ItemSection className="item-section story-section">
          <h2>📖 背后的故事</h2>
          <p>{item.story}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>🔍 发现过程</h2>
          <p>{item.discoveryStory}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>ℹ️ 额外信息</h2>
          <p>{item.additionalInfo}</p>
        </ItemSection>

        <ItemSection className="item-section">
          <h2>📍 基本信息</h2>
          <p>位置: {item.location}</p>
          <p>发现者: {item.discoveredBy}</p>
          <p>发现时间: {item.discoveryDate}</p>
        </ItemSection>
      </PrintableContent>

      <PrintButton onClick={handlePrint}>
        🖨️ 打印页面
      </PrintButton>
    </DetailContainer>
  );
}

export default ItemDetail;
